[Form]
public class HMPatientChargeEntrySearch extends FormRun
{
    RecId  physicianRecId;
    boolean isChecked;
}

public void run()
{
    super();
    //todo: need to decide when to enable follwoing code

   // AllProviderCheckBox.value(false);
    //PhysicianDropdown.allowEdit(true);
    SearchDate.dateValue(DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()));
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.checked())
        {
            isChecked = true;
            physicianRecId = 0;
            PhysicianDropdown.text("");
            PhysicianDropdown.allowEdit(false);
        }
        else
        {
            isChecked = false;
            PhysicianDropdown.allowEdit(true);
        }
    }

    return ret;
}

public void jumpRef()
{
    Args                            args;
    MenuFunction                    menuFunction;

    args = new Args();
    args.caller(element);

    menuFunction = new MenuFunction(menuitemdisplaystr(HMClinicStaffMembers),MenuItemType::Display);
    menuFunction.run(args);

}

public void lookup()
{
    Args args = new Args();
    FormRun itemLookUp;

    args.name(formstr(HMPhysicianAdvanceLookup));
    args.caller(element);
    args.parmEnumType(enumNum(HMAdvanceStaffLookupParm));
    args.parmEnum(HMAdvanceStaffLookupParm::Physician);
    itemLookUp = ClassFactory.formRunClass(args);
    itemLookUp.init();
    this.performFormLookup(itemLookUp);
}

public boolean modified()
{
    HMClinicStaff           hmClinicStaffLocal;
    HMlookupClass           HMlookupClassObj = new HMlookupClass();

    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.text() != "")
        {
            hmClinicStaffLocal =  HMlookupClassObj.checkValidPhysicianName(this.text());

            if (hmClinicStafflocal.RecId)
            {
               element.storePhysician(hmClinicStafflocal.RecId);
            }
            else
            {
                warning(strFmt("@SYS316757",'Physician'));
                PhysicianDropdown.text(HMClinicStaff::getPhysicianNameByRecId(physicianRecId));
            }
        }
        else
        {
            element.storePhysician(0);
        }

    }

    return ret;
}

