[Form]
public class HMPatientInsurance extends FormRun
{
    HMPatientRecId          patientRecId;
    RecId                   subscriberRecId;
    HMPayerType payerType;
    #define.MinReaValue(0.00)
    #ISOCountryRegionCodes
}

public void close()
{


    super();

}

public void enableDisableSubscriberFields()
{
    boolean enable = (HMPatientInsurance.SubscriberRelation != PatientInsuranceRelation::Self);
    HMPatientInsurance_SubscriberName.enabled(enable);
    HMPatientInsurance_SubscriberDateOfBirth.enabled(enable);
    HMPatientInsurance_SubscriberEmployer.enabled(enable);
}

private void getPatient(Common _selectedRecord)
{

    HMPatientInsurance.InsuranceCarrier = CustTable::findRecId(HMPatient::find(_selectedRecord.RecId).CustTable).AccountNum;
    HMPatientInsurance_InsuranceCarrier.update();
    HMPatientInsurance_InsuranceCarrier.text(HMPatientInsurance.InsuranceCarrier);
    HMPatientInsurance_Name.update();
    InsurancePostalAddress.update();
    HMInsuranceCarrier_Phone.update();
    HMInsuranceCarrier_Fax.update();
    HMInsuranceCarrier_Email.update();
    patientRecId = _selectedRecord.RecId;
}

private void getSubscriber(Common _selectedRecord)
{
    if(HMPatientInsurance.HMPayerType == HMPayerType::InsuranceCarrier)
    {
        HMPatientInsurance.HMPatientSubscriber = _selectedRecord.RecId;
        //subscriberRecId = _selectedRecord.RecId;
        SubscriberName.text(HMPatient::find(_selectedRecord.recId).patientName());
        this.setSubscriberRelation();
    }
    else
    {
        warning("@MAZ6175");
    }

}

public void init()
{
    super();
    if(element.args().openMode() == OpenMode::Edit)
    {
        NewButton.enabled(false);
    }
    //this.updateDesign();
    this.setPolicyTypes();
