    {
        HMPatientInsurance_ds.allowCreate(false);
        DeleteButton.enabled(false);
        HMNew_Patient.enabled(false);
    }

    // <IN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        InsuranceCarrier.frameType(FormFrameType::None);
        GridGroup.visible(false);
        VSplitter.visible(false);
        ActionPane.visible(false);
        HMPatientInsurance_HMPayerType.allowEdit(false);
        HMPatientInsurance_PolicyPlan.visible(false);
        HMNew_Patient.visible(false);
        HMPatientInsurance_Relation.visible(false);
        HMPatientInsurance_InsuranceNotes.visible(false);
        HMPatientInsurance_GroupNumber.visible(false);
        HMPatientInsurance_GroupName.visible(false);
        Policy_PolicyGroup.visible(false);
        Policy_PolicyPlan.visible(false);
        CoveragePlan.visible(false);
        SubscriberName.visible(false);
        HMPatientInsurance_SubscriberID.visible(false);
        HMPatientInsurance_SubscriberRelation.visible(false);
        PolicyHolder.visible(false);
        HMPatientInsurance_EligibilityComments.visible(false);
        HMPatientInsurance_AuthorizationRequest.visible(false);
        HMPatientInsurance_AuthorizationExpiryDate.visible(false);
        HMPatientInsurance_AuthorizationNotes.visible(false);
        PayerTypeGroup.visible(false);
        HMPatientInsurance_ds.allowDelete(false);
        DialogCommit.visible(true);
        HMPatientInsurance_InsuranceStatus.visible(false);
        element.design().statusBarStyle(FormStatusBarStyle::None);
        HMInsuranceCarrier_ContactPoint.visible(false);
        InsurancePostalAddress.visible(false);
        HMInsuranceCarrier_Phone.visible(false);
        HMInsuranceCarrier_Fax.visible(false);
