    // </IN>
}

private void resetInsurancePolicyDetails()
{
    HMPatientInsurance.GroupName = '';
    HMPatientInsurance.GroupNumber = '';
    HMPatientInsurance.PolicyGroup = '';
    HMPatientInsurance.PolicyNumber = '';
    HMPatientInsurance.PolicyPlan = '';
    HMPatientInsurance.HMInsuranceCoveragePlan = 0;
    HMPatientInsurance.SubscriberRelation = PatientInsuranceRelation::None;
    HMPatientInsurance.HMPatientSubscriber = 0;
    HMPatientInsurance.SubscriberID = '';
    HMPatientInsurance.DeductibleAmount = 0;
    HMPatientInsurance.EffectiveDate = dateNull();
    HMPatientInsurance.DeductibleUpdateDate = dateNull();
    HMPatientInsurance.CurrentDeductibleAmount = 0;
    HMPatientInsurance.HMInsuranceEligibilityStatus = 0;
    HMPatientInsurance.EligibilityLastChecked = dateNull();
    HMPatientInsurance.EligibilityComments = '';
    HMPatientInsurance.PreauthorizationReqd = NoYes::No;

    HMPatientInsurance.InsuranceEligibilityStatus = HMEligibilityStatus::None;
}

public void run()
{
    super();

    // <IN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        if(HMPatientInsurance.HMPayerType != payerType)
        {
            HMPatientInsurance.HMPayerType = payerType;
            HMPatientInsurance.InsuranceCarrier = '';
        }
    }
    // </IN>

    element.enableDisableSubscriberFields();
}

public void selfSubscriberUpdated()
{
    HMPatient hmPatient;

    element.enableDisableSubscriberFields();

    if (HMPatientInsurance.SubscriberRelation == PatientInsuranceRelation::Self)
    {
        hmPatient                                = HMPatient::find(HMPatientInsurance.HMPatient);
        HMPatientInsurance.SubscriberName        = hmPatient.patientName();
        HMPatientInsurance.SubscriberDateOfBirth = hmPatient.patientBirthDate();
        HMPatientInsurance.SubscriberEmployer    = hmPatient.EmployeeNumber;
    }
    else
    {
        HMPatientInsurance.SubscriberName        = '';
        HMPatientInsurance.SubscriberDateOfBirth = dateNull();
        HMPatientInsurance.SubscriberEmployer    = '';
    }
}

public void setPolicyHolder()
{
    if(hmPatientInsurance.SelfPolicy == NoYes::Yes)
    {
        GuarantorGroup.enabled(false);
        //HMPatientInsurance.Guarantor=CustTable::findRecId(hmPatient::find(HMPatientInsurance.HMPatient).CustTable).AccountNum;

    }
    else
    {
        GuarantorGroup.enabled(true);
    }
}

public void setPolicyTypes()
{
    if(HMPatientInsurance.HMPayerType == HMPayerType::InsuranceCarrier ||HMPatientInsurance.HMPayerType == HMPayerType::Corporate)
    {
        if(HMPatientInsurance.PolicyType == PolicyType::CoInsurance)
        {
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).allowEdit(false);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).allowEdit(true);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).mandatory(false);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).mandatory(true);
            HMPatientInsurance.CoPayAmount = #MinReaValue;
        }
        else if(HMPatientInsurance.PolicyType == PolicyType::CoPay)
        {
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).allowEdit(true);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).allowEdit(false);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).mandatory(true);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).mandatory(false);
            HMPatientInsurance.CoInsurance = #MinReaValue;
        }
        else if(HMPatientInsurance.PolicyType == PolicyType::None)
        {
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).mandatory(false);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).allowEdit(false);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).mandatory(false);
            HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).allowEdit(false);
            HMPatientInsurance.CoPayAmount = #MinReaValue;
            HMPatientInsurance.CoInsurance = #MinReaValue;
        }

        // <US>
        //if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoUS]))
        //{
            //HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, EffectiveDate)).mandatory(true);
            //HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, EndDate)).mandatory(true);
        //}
        // </US>

        HMPatientInsurance_HMCardIssueNumber.allowEdit(true);
        HMPatientInsurance_HealthPlanId.allowEdit(true);
        HMPatientInsurance_BalanceAmount.allowEdit(true);
        HMPatientInsurance_EligibilityComments.allowEdit(true);
        HMPatientInsurance_EligibilityAmount.allowEdit(true);
        HMPatientInsurance_AuthorizationExpiryDate.allowEdit(true);
        HMPatientInsurance_AuthorizationNotes.allowEdit(true);
        HMPatientInsurance_AuthorizationRequest.allowEdit(true);
        HMPatientInsurance_AuthorizationLimit.allowEdit(true);
        HMPatientInsurance_AuthorizationDate.allowEdit(true);
        HMPatientInsurance_AssignmentOfBenefitsIndicator1.allowEdit(true);
        HMPatientInsurance_ReleaseofInformationCertificationIndicat.allowEdit(true);
        HMPatientInsurance_HMPatientInsuranceMembershipId.allowEdit(true);
        ReferenceGroupPlans.allowEdit(true);
        //ReferenceGroup.visible(true);
        //ReferenceGroupPlans.mandatory(true);
        //ReferenceGroup.mandatory(true);
        Policy_PolicyNumber.mandatory(true);
        //HMPatientInsurance_HMPatientInsuranceMembershipId.mandatory(true);
        HMPatientInsurance_HMEligibleClass.allowEdit(true);
        HMPatientInsurance_PriorAuthorizationNumber.allowEdit(true);
    }
    else
    {
        HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoPayAmount)).mandatory(false);
        HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, CoInsurance)).mandatory(false);
        HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, EffectiveDate)).mandatory(false);
        HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, EndDate)).mandatory(false);
    }
    if(HMPatientInsurance.HMPayerType == HMPayerType::Corporate)
    {
        HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, EmployeeID)).mandatory(true);
    }
    else
    {
        HMPatientInsurance_ds.object(fieldNum(HMPatientInsurance, EmployeeID)).mandatory(false);
    }
    if(HMPatientInsurance.HMPayerType==HMPayerType::Gurantor)
    {
        HMPatientInsurance_HMCardIssueNumber.allowEdit(false);
        HMPatientInsurance_HealthPlanId.allowEdit(false);
        HMPatientInsurance_BalanceAmount.allowEdit(false);
        HMPatientInsurance_EligibilityComments.allowEdit(false);
        HMPatientInsurance_EligibilityAmount.allowEdit(false);
        HMPatientInsurance_AuthorizationExpiryDate.allowEdit(false);
        HMPatientInsurance_AuthorizationNotes.allowEdit(false);
        HMPatientInsurance_AuthorizationRequest.allowEdit(false);
        HMPatientInsurance_AuthorizationLimit.allowEdit(false);
        HMPatientInsurance_AuthorizationDate.allowEdit(false);
        HMPatientInsurance_AssignmentOfBenefitsIndicator1.allowEdit(false);
        HMPatientInsurance_ReleaseofInformationCertificationIndicat.allowEdit(false);
        HMPatientInsurance_HMPatientInsuranceMembershipId.allowEdit(false);
        ReferenceGroupPlans.allowEdit(false);
        //ReferenceGroup.visible(false);
        ///(false);
        //ReferenceGroup.mandatory(false);
        Policy_PolicyNumber.mandatory(false);
        //HMPatientInsurance_HMPatientInsuranceMembershipId.mandatory(false);
        HMPatientInsurance_HMEligibleClass.allowEdit(false);
        HMPatientInsurance_PriorAuthorizationNumber.allowEdit(false);
    }

