public void updateAuthorization()
{
    if(HMPatientInsurance.PreauthorizationReqd)
    {
        HMPatientInsurance_PriorAuthorizationNumber.allowEdit(true);
    }
    else
    {
        HMPatientInsurance.PriorAuthorizationNumber = '';
        HMPatientInsurance_PriorAuthorizationNumber.allowEdit(false);
    }
}

