public boolean validateWrite()
{
    boolean ret;
    HMInsuranceContractCarrierCombination   combination;
    HMInsuranceContract                     contract;
    HMInsuranceContractClassOrPlans         plans;
    RefRecId                                carrierRecId;

    ret = super();
    if(ret)
    {
        carrierRecId = HMInsuranceCarrier::findByCode(HMPatientInsurance_InsuranceCarrier.valueStr()).RecId;

        select firstOnly combination
            where combination.HMInsuranceCarrier == carrierRecId;


        if(HMPatientInsurance.HMPayerType == HMPayerType::InsuranceCarrier || HMPatientInsurance.HMPayerType == HMPayerType::Corporate)
        {
            if (combination)
            {
                select firstOnly HMInsuranceContract
                    join combination
                    where HMInsuranceContract.RecId == HMPatientInsurance.HMInsuranceContract
                        && HMInsuranceContract.HMInsuranceContractCarrierCombination == combination.recid
                        && combination.HMInsuranceCarrier   == carrierRecId;

                //if (HMInsuranceContract)
                //{
                    plans.clear();

                    if (HMInsuranceContract.HMContractStatus == HMContractStatus::Active)
                    {
                        select plans
                            where plans.RecId == HMPatientInsurance.HMInsuranceContractClassOrPlans
                                && plans.HMInsuranceContract == HMInsuranceContract.RecId;

                        //if (plans)
                        //{

                            if (plans.HMContractClassStatus == HMContractClassStatus::Active)
                            {
                                if(!HMPatientInsurance.PolicyNumber)
                                {
                                    ret = checkFailed("Policy number must be filled in.");
                                }
                                else
                                {
                                    ret = true;
                                }
                            }
                            //else
                            //{
                                //ret = checkFailed("@MAZ13184");
                            //}
                        //}
                        else
                        {
                            //ret = checkFailed("@MAZ13158");
                        }
                    }
                    else
                    {
                        //ret = checkFailed("@MAZ13185");
                    }
                //}
                //else
                //{
                    //ret = checkFailed("@MAZ13160");
                //}
            }
            else
            {
                //ret = checkFailed("@MAZ13159");
            }
            // <US>
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoUS]))
            {
                if(HMPatientInsurance.EndDate < HMPatientInsurance.EffectiveDate)
                {
                    warning("@MAZ6174");
                    ret = false;
                }
            }
            // </US>

        }
        if(HMPatientInsurance.HMPatientSubscriber != 0)
        {
            if(HMPatientInsurance.SubscriberRelation == PatientInsuranceRelation::None)
            {
                warning("@MAZ7185");
                ret = false;
            }
        }

        if(HMPatientInsurance.PreauthorizationReqd && HMPatientInsurance.PriorAuthorizationNumber == '')
        {
            warning(strFmt("@SYS84753","@MAZ7220"));
            ret = false;
        }
    }
    return ret;
}

public void init()
{
    FormRun     callerObj;
    HMPatient   patient;
    Common      custRecord;
    //HMPatientCategory category;
    super();
    callerObj = element.args().caller();

    if(callerObj.args().dataset() == tableNum(CustTable))
    {
        custRecord = callerObj.args().record();
        select * from patient where patient.CustTable == custRecord.RecId;
        patientRecId = patient.RecId;
    }
    else if(callerObj.args().dataset() == tableNum(HMPatient))
    {
        patientRecId = callerObj.args().record().RecId;
    }


    patientRecId = str2int64(element.args().parm());
    this.query().dataSourceTable(tablenum(HMPatientInsurance)).addRange(fieldNum(HMPatientInsurance,HMPatient)).value(int642str(patientRecId));
    this.query().dataSourceTable(tablenum(HMPatientInsurance)).addSortField(fieldNum(HMPatientInsurance,Priority),SortOrder::Ascending);

    /* Bupa doesnt require category field
    // <IN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        category = HMPatient::find(patientRecId).Category;

        if(category == HMPatientCategory::Corporate)
        {
            payerType = HMPayerType::Corporate;
            element.design().caption("@MAZ9302");
        }
        else if(category == HMPatientCategory::Insurance)
        {
            payerType = HMPayerType::InsuranceCarrier;
        }
    }*/
    // </IN>

}

public void write()
{
    HMPatientInsurance      hmPatientInsuranceLocal,hmPatientInsurancePriorityCheck;
    int                     lastCount;
    int                     patientCount;
    RecId                   selectedInsuranceRecId;
    select * from hmPatientInsurancePriorityCheck
            order by hmPatientInsurancePriorityCheck.Priority desc
            where hmPatientInsurancePriorityCheck.HMPatient ==patientRecId;
    super();
    while select hmPatientInsuranceLocal
        order by hmPatientInsuranceLocal.Priority desc
        where hmPatientInsuranceLocal.HMPatient == patientRecId
    {
        if(hmPatientInsuranceLocal.HMPayerType != HMPayerType::Patient)
        {
            lastCount = hmPatientInsuranceLocal.Priority;
            break;
        }
        else
        {
            continue;
        }
    }

    if(lastCount > 1)
    {
        if(hmPatientInsurancePriorityCheck.HMPayerType == HMPayerType::Patient)
        {
            while select forUpdate hmPatientInsuranceLocal
                order by hmPatientInsuranceLocal.Priority desc
                where hmPatientInsuranceLocal.HMPatient ==patientRecId
            {

                if(hmPatientInsuranceLocal.Priority == 1 && hmPatientInsuranceLocal.HMPayerType != HMPayerType::Patient)
                {
                    break;
                }
                else
                {
                    if(hmPatientInsuranceLocal.HMPayerType == HMPayerType::Patient)
                    {
                        patientCount = 1;
                        if(HMPatientInsurance.HMPayerType != HMPayerType::Patient)
                        {
                            HMPatientInsurance::updatePriorityOrder(hmPatientInsuranceLocal,true);
                        }
                        while(patientCount != lastCount)
                        {
                            patientCount++;

                        }
                        lastCount--;
                    }
                }
            }
        }
    }
    selectedInsuranceRecId = HMPatientInsurance.RecId;

    HMPatientInsurance_DS.executeQuery();
    HMPatientInsurance_DS.positionToRecord(HMPatientInsurance::find(selectedInsuranceRecId));
    element.args().caller().refreshPatientInsurance();

}

display DirPersonBirthDate PatientBirthDate()
{
    HMPatient       hmPatientLocal;
    CustTable       custTableLocal;
    DirPerson       dirPersonLocal;

    if (HMPatientInsurance.SelfPolicy == NoYes::Yes)
    {
    select * from hmPatientLocal join custTableLocal join dirPersonLocal
    where hmPatientLocal.RecId == patientRecId
        &&  hmPatientLocal.CustTable == custTableLocal.RecId
        &&  custTableLocal.Party == dirPersonLocal.RecId;
    }
    else
    {
        select * from custTableLocal join dirPersonLocal
        where custTableLocal.AccountNum == HMPatientInsurance.InsuranceCarrier
            &&  custTableLocal.Party == dirPersonLocal.RecId;
    }
    return dirPersonLocal.birthDate();
}

public int active()
{
    int ret;

    ret = super();

    if(ret)
    {
        element.updateSubscriberName();
        element.setPolicyHolder();
        element.setPolicyTypes();

        // <IN>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
        {
            element.updateDesign_IND();
        }
        // </IN>
        else
        {
            element.updateAuthorization();
            element.updateActiveDSDesign();
        }

    }
    return ret;
}

display DirPartyName PatientName()
{
    HMPatient       hmPatientLocal;
    CustTable       custTableLocal;
    DirPartyTable   dirPartyTableLocal;

    select * from hmPatientLocal join custTableLocal join dirPartyTableLocal
    where hmPatientLocal.RecId == patientRecId
        &&  hmPatientLocal.CustTable == custTableLocal.RecId
        &&  custTableLocal.Party == dirPartyTableLocal.RecId;

    return dirPartyTableLocal.Name;
}

