public void performFormLookup(xFormRun _form, FormControl _formControl)
{
    FormDataSource formDataSource;
    QueryRun queryRunGenerated,qr;
    Query q;
    QueryBuildDataSource qbd;
    QueryBuildRange qbr;

    ;

    // The FormDataSource object retrieved from the FormRun parameter.
    formDataSource = _form.dataSource(1);
    // Get the generated query.
    queryRunGenerated = formDataSource.queryRun();

    q = queryRunGenerated.query();

    qbd = q.dataSourceTable(tableNum(CustTable));
    qbr = qbd.addRange(fieldNum(CustTable,CustType));
    qbr.value(SysQuery::value(CustType::Insurance));
    // Modify the query.
    // ...
    qr = new QueryRun(q);
    formDataSource.queryRun(qr);
    // Call the standard implementation of performFormLookup
    // with the parameters supplied in this method.

    super(_form, _formControl);
}

