void clicked()
{
    DialogButton                        dialogButton;
    HMPatientInsurance                  hmPatientInsuranceDelete;
    HMPatientInsurance                  hmPatientInsuranceLocalNew;
    boolean                             isMarked = false;

    super();

    if(HMPatientInsurance.RecId == 0)
    {
        HMPatientInsurance_ds.delete();
    }
    else
    {
        dialogButton=Box::yesNo(strFmt("@MAZ3234","@MAZ1254"),DialogButton::No);

        if(dialogButton==DialogButton::Yes)
        {
            hmPatientInsuranceDelete = HMPatientInsurance_ds.getFirst(1);
            while (hmPatientInsuranceDelete)
            {
                isMarked = true;
                ttsbegin;
                hmPatientInsuranceLocalNew=HMPatientInsurance::find(hmPatientInsuranceDelete.recId,true);
                if(hmPatientInsuranceLocalNew.validateDelete())
                {
                    hmPatientInsuranceLocalNew.delete();
                }
                ttsCommit;
                hmPatientInsuranceDelete = HMPatientInsurance_ds.getNext();
            }

            if(HMPatientInsurance.RecId && !(isMarked))
            {
                ttsbegin;
                hmPatientInsuranceLocalNew=HMPatientInsurance::find(HMPatientInsurance.recId,true);
                if(hmPatientInsuranceLocalNew.validateDelete())
                {
                    hmPatientInsuranceLocalNew.delete();
                }
                ttsCommit;
            }
        }
    }

    HMPatientInsurance_ds.executeQuery();
    element.args().caller().refreshPatientInsurance();
}

