public boolean modified()
{
    boolean         ret;
    RecId           recId;
    HMPatient       hmPatient;
    CustTable       custTableLocal;
    DirPartyTable   dirParty;
    Name            patientName;
    ret = super();
    if(ret)
    {
        recId = HMPatientInsurance.HMPatientSubscriber;
        if(this.text() == '')
        {
            HMPatientInsurance.HMPatientSubscriber = 0;
        }
        else
        {
            patientName = this.text();
            select recId from hmPatient
            join AccountNum,Party from custTableLocal
            join Name from dirParty
            where hmPatient.CustTable == custTableLocal.RecId
            && custTableLocal.Party == dirParty.RecId
            && dirParty.Name == patientName;

            if(hmPatient.RecId != 0)
            {
                this.text(HMPatient::find(hmPatient.RecId).patientName());
                HMPatientInsurance.HMPatientSubscriber = hmPatient.RecId;
            }
            else
            {

                this.text(HMPatient::find(recId).patientName());
                HMPatientInsurance.HMPatientSubscriber = recId;
                warning("@MAZ6167");
            }
        }
        element.setSubscriberRelation();
    }
    return ret;
}

