void clicked()
{
    //super();

    Args    args;
    EcoResProductMaster master;

    args = new Args();

    select * from master where master.RecId == ecoResProduct.RecId;
    args.record(master);
    args.caller(element);
    new MenuFunction(menuitemDisplayStr(RetailMatrixPivot), MenuItemType::Display).run(args);
}

void clicked()
{
    FormRun formRun;
    Object object;

    super();

    formRun = element.args().caller();

    if(formHasMethod(formRun,'setProcedureLocal'))
    {
        object = formRun;
        object.setProcedureLocal(InventTable.ItemId);
    }
    else if(formHasMethod(formRun,'storeItem'))
    {
        object = formRun;
        object.storeItem(InventTable.ItemId);
    }

    okCalled = true;
    element.close();
}
