public void closeOk()
{
    if(this.validateAmounts())
    {
        element.args().caller().applyPayments(ApprovedAmount.realValue(),
                                            CopayAmount.realValue(),
                                            CoInsuranceAmount.realValue(),
                                            DeductibleAmount.realValue(),
                                            HMEOBReasonCode::findRecId(EOBReasonCode.valueStr()),
                                            TransferAmount.realValue(),
                                            WriteOffAmount.realValue(),
                                            PaymentAmount.realValue(),
                                            AdditionalAmount.realValue(),
                                            DocumentControlNumber.text());
        super();
    }

}

public boolean validateAmounts()
{
    if(PaymentAmount.realValue() + CoPayAmount.realValue() + CoInsuranceAmount.realValue() + DeductibleAmount.realValue() + TransferAmount.realValue() + WriteOffAmount.realValue() + AdditionalAmount.realValue() != AmountAvailableToPay.realValue())
    {
        return checkFailed("Amount not matching with claim amount");
    }
    if(AdditionalAmount.realValue() != 0 && EOBReasonCode.text() != '')
    {
        return checkFailed("Cannot set reason code incase of additional amount");
    }
    return true;
}

public boolean modified()
{
    boolean ret;
    real additionalAmountValue;
    ret = super();
    element.updatePaymentAmount();
    additionalAmountValue = AmountAvailableToPay.realValue() - ApprovedAmount.realValue();
    if(additionalAmountValue < 0)
    {
        additionalAmount.realValue(additionalAmountValue);
    }
    else
    {
        additionalAmount.realValue(0);
    }
    return ret;
}

