public void lookup()
{
   Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(HMEOBReasonCode), this);

    sysTableLookup.addLookupField(fieldNum(HMEOBReasonCode, Code));
    sysTableLookup.addLookupField(fieldNum(HMEOBReasonCode, Description));

    queryBuildDataSource = query.addDataSource(tableNum(HMEOBReasonCode));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;
    HMInvoiceNextAction HMInvoiceNextAction;
    ret = super();
    if(ret)
    {
        HMInvoiceNextAction = HMEOBReasonCode::findNextInvoiceAction(HMEOBReasonCode::findRecId(this.text()));
        if(HMInvoiceNextAction == HMInvoiceNextAction::TransferAndWriteOff)
        {
            TransferAmount.allowEdit(true);
            WriteOffAmount.allowEdit(true);
        }
        else if(HMInvoiceNextAction == HMInvoiceNextAction::WriteOff
        || HMInvoiceNextAction == HMInvoiceNextAction::Discount)
        {
            TransferAmount.allowEdit(false);
            WriteOffAmount.allowEdit(true);
        }
        else if (HMInvoiceNextAction == HMInvoiceNextAction::TransferToInsurance
        || HMInvoiceNextAction == HMInvoiceNextAction::TransferToPatient)
        {
            TransferAmount.allowEdit(true);
            WriteOffAmount.allowEdit(false);
        }
        else
        {
            TransferAmount.allowEdit(false);
            WriteOffAmount.allowEdit(false);
            TransferAmount.realValue(0.00);
            WriteOffAmount.realValue(0.00);
        }
    }
    return ret;
}

