public void updateBatchAmountDetailFields(str _batchId = BatchNumber.text())
{
    container result;
    result = HMPaymentCentreFormHandler.getClaimAmountDetailsByBatchNumber(_batchId);
    ClaimAmount.realValue(conPeek(result,1));
    PaymentReceived.realValue(conPeek(result,2));
    DiscountAmountApplied.realValue(conPeek(result,3));
    WriteOffAmountApplied.realValue(conPeek(result,4));
    RemainingPayment.realValue(conPeek(result,5));
}

[SysClientCacheDataMethodAttribute(true)]
public display AmountMST netInsuranceAmount(HMPatientChargeEntryTable _HMPatientChargeEntryTable)
{
    HMCaseTrans hmCaseTransLocal;
    hmPatientChargeEntryLine hmPatientChargeEntryLineLocal;

    select sum(InsuranceAmount) from hmCaseTransLocal
    join hmPatientChargeEntryLineLocal where hmPatientChargeEntryLineLocal.HMCaseTrans == hmCaseTransLocal.RecId
    && hmPatientChargeEntryLineLocal.HMPatientChargeEntryTable == _HMPatientChargeEntryTable.RecId
    && hmPatientChargeEntryLineLocal.HMInsuranceCarrierBatchDetails == batchRecId
    && (hmCaseTransLocal.BillingStatus != HMBillStatus::NotPaid
    && hmCaseTransLocal.BillingStatus != HMBillStatus::Reveresed);


    return hmCaseTransLocal.InsuranceAmount;
}

        [SysClientCacheDataMethodAttribute(true)]
        public display AmountMST netAmount(HMPatientChargeEntryTable _HMPatientChargeEntryTable)
        {
            HMCaseTrans					hmCaseTransLocal;
            hmPatientChargeEntryLine    hmPatientChargeEntryLineLocal;
            HMClaimChargeLine           hmClaimChargeLine;

            select sum(Discount), sum(DiscountAmount), sum(TotalAmount) from hmCaseTransLocal
            join hmPatientChargeEntryLineLocal where hmPatientChargeEntryLineLocal.HMCaseTrans == hmCaseTransLocal.RecId
            join HMClaimChargeLine where HMClaimChargeLine.HMPatientChargeEntryLine == hmPatientChargeEntryLineLocal.RecId
			&& hmPatientChargeEntryLineLocal.HMPatientChargeEntryTable == _HMPatientChargeEntryTable.RecId
            && hmPatientChargeEntryLineLocal.HMInsuranceCarrierBatchDetails == batchRecId
            && (hmCaseTransLocal.BillingStatus != HMBillStatus::NotPaid
            && hmCaseTransLocal.BillingStatus != HMBillStatus::Reveresed);

            return hmCaseTransLocal.TotalAmount - hmCaseTransLocal.DiscountAmount;
        }

