/// <summary>
/// Gets or sets whether the open transaction is marked for settlement.
/// </summary>
/// <param name="_set">
/// true if marked for settlement is set; otherwise, false.
/// </param>
/// <param name="_hmPatientChargeEntryTable">
/// The current instance of the HMPatientChargeEntryTable table.
/// </param>
/// <param name="_isMarkedForSettlement">
/// The new marked for settlement when the <paramref name="_set"/> is true.
/// </param>
/// <returns>
/// Whether the record is marked for discount/write/posting.
/// </returns>

// BP deviation documented
public edit boolean editIsMarked(
    boolean _set,
    HMPatientChargeEntryTable _hmPatientChargeEntryTable,
    boolean _isMarked)
{
    boolean isMarked;
    if(_set)
    {
        if(_isMarked)
        {
            if(!markedChargeEntry.exists(_hmPatientChargeEntryTable.RecId))
            {
                markedChargeEntry.insert(_hmPatientChargeEntryTable.RecId,_hmPatientChargeEntryTable);
                HMPaymentCentreFormHandler.updateChargeEntryLine(HMPatientChargeEntryLinesTmp,_hmPatientChargeEntryTable.RecId,NoYes::Yes);
            }
        }
        else
        {
            if(markedChargeEntry.exists(_hmPatientChargeEntryTable.RecId))
            {
                markedChargeEntry.remove(_hmPatientChargeEntryTable.RecId);
                HMPaymentCentreFormHandler.updateChargeEntryLine(HMPatientChargeEntryLinesTmp,_hmPatientChargeEntryTable.RecId,NoYes::No);
            }
        }
    }

    isMarked = markedChargeEntry.exists(_hmPatientChargeEntryTable.RecId);
    HMPatientChargeEntryLine_ds.executeQuery();

    return isMarked;
}

