        [SysClientCacheDataMethodAttribute(true)]
        public display AmountMST totalAmountWithDiscWrite(HMPatientChargeEntryTable _HMPatientChargeEntryTable)
        {
            hmCaseTrans					hmCaseTransLocal;
            hmPatientChargeEntryLine	hmPatientChargeEntryLineLocal;
            HMClaimChargeLine			hmClaimChargeLine;

            select sum(TotalAmount), sum(WriteOff) , sum(Discount), sum(DiscountAmount) from hmCaseTransLocal
            join hmPatientChargeEntryLineLocal where hmPatientChargeEntryLineLocal.hmCaseTrans == hmCaseTransLocal.RecId
            join HMClaimChargeLine where HMClaimChargeLine.HMPatientChargeEntryLine == hmPatientChargeEntryLineLocal.RecId
			&& hmPatientChargeEntryLineLocal.HMPatientChargeEntryTable == _HMPatientChargeEntryTable.RecId
            && hmPatientChargeEntryLineLocal.HMInsuranceCarrierBatchDetails == batchRecId
            && (hmCaseTransLocal.BillingStatus != HMBillStatus::NotPaid
            && hmCaseTransLocal.BillingStatus != HMBillStatus::Reveresed);

            return hmCaseTransLocal.TotalAmount - hmCaseTransLocal.WriteOff - hmCaseTransLocal.Discount - hmCaseTransLocal.DiscountAmount;
        }

