public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource,qbdsTPA;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(custTable), this);

    sysTableLookup.addLookupField(fieldNum(CustTable, AccountNum));
    sysTableLookup.setLabel("@MAZ13168");
    sysTableLookup.addLookupMethod(tableMethodStr(CustTable, name));

    queryBuildDataSource = query.addDataSource(tableNum(CustTable));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(CustTable, CustType));
    queryBuildRange.value(SysQuery::value(CustType::Insurance));
    queryBuildDataSource.addDataSource(tableNum(HMInsuranceCarrier)).relations(true);
    qbdsTPA = query.dataSourceTable(tableNum(HMInsuranceCarrier)).addDataSource(tableNum(HMTpaDetails));
    qbdsTPA.relations(true);
    if(TPAId.text() == '')
    {
        qbdsTPA.joinMode(JoinMode::NoExistsJoin);
    }
    else
    {
        qbdsTPA.joinMode(JoinMode::InnerJoin);
        qbdsTPA.addRange(fieldNum(HMTpaDetails,TPA)).value(SysQuery::value(HMTPA::findRecIdByCode(TPAId.text())));
    }

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();

    //super();
}

public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource;
    QueryBuildRange queryBuildRange;
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(HMInsuranceCarrierBatchDetails), this);

    if(InsuranceId.valueStr() == '')
    {
        warning("@MAZ13537");
        return;
    }

    sysTableLookup.addLookupField(fieldNum(HMInsuranceCarrierBatchDetails, BatchNumber));
    sysTableLookup.addLookupField(fieldNum(HMInsuranceCarrierBatchDetails, FromDate));
    sysTableLookup.addLookupField(fieldNum(HMInsuranceCarrierBatchDetails, ToDate));
    sysTableLookup.addLookupField(fieldNum(HMInsuranceCarrierBatchDetails, Reference));

    queryBuildDataSource = query.addDataSource(tableNum(HMInsuranceCarrierBatchDetails));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(HMInsuranceCarrierBatchDetails, HMInsuranceCarrier));
    queryBuildRange.value(SysQuery::value(HMInsuranceCarrier::findByCode(InsuranceId.valueStr()).RecId));

    queryBuildDataSource.addRange(fieldNum(HMInsuranceCarrierBatchDetails, Status)).value(SysQuery::value(HMInsuranceCarrierBatchStatus::Active));

    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;

    ret = super();
    batchNumberId = this.text();
    if(ret)
    {
        HMInsuranceCarrierBatchDetails_ds.positionToRecord(HMInsuranceCarrierBatchDetails::findbByBatchNumber(batchNumberId));
        batchRecId = HMInsuranceCarrierBatchDetails.RecId;
        element.updateBatchAmountDetailFields(batchNumberId);
        qbrChargeEntryRecid.enabled(true);
    }
    HMPatientChargeEntryTable_ds.executeQuery();
    HMPatientChargeEntryLinesTmp_ds.executeQuery();
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        switch(this.selection())
        {
            case HMPaymentCenterClaimStatus::Submitted:
                qbrClaimStatus.enabled(true);
                qbrClaimStatus.value(SysQuery::value(HMClaimStatus::Submitted));
                break;
            case HMPaymentCenterClaimStatus::NotSubmitted:
                qbrClaimStatus.enabled(true);
                qbrClaimStatus.value(SysQuery::valueNot(HMClaimStatus::Submitted));
                break;
            case HMPaymentCenterClaimStatus::Both:
                qbrClaimStatus.enabled(false);
                break;
        }
        HMPatientChargeEntryTable_ds.executeQuery();
    }
    return ret;
}

void clicked()
{
    HMPatientChargeEntryTable HMPatientChargeEntryTableLocal;
    HMPatientChargeEntryTableLocal = HMPatientChargeEntryTable_ds.getFirst();
    while(HMPatientChargeEntryTableLocal)
    {
        if(!markedChargeEntry.exists(HMPatientChargeEntryTableLocal.RecId))
        {
            markedChargeEntry.insert(HMPatientChargeEntryTableLocal.RecId,HMPatientChargeEntryTableLocal);
            HMPaymentCentreFormHandler.updateChargeEntryLine(HMPatientChargeEntryLinesTmp,HMPatientChargeEntryTableLocal.RecId,NoYes::Yes);
        }
        HMPatientChargeEntryTableLocal = HMPatientChargeEntryTable_ds.getNext();
    }
    HMPatientChargeentrytable_ds.executeQuery();
}

void clicked()
{
    markedChargeEntry = new Map(Types::Int64,Types::Record);
    HMPaymentCentreFormHandler.unMarkAllChargeEntryLines(HMPatientChargeEntryLinesTmp);
    HMPatientChargeentrytable_ds.executeQuery();
}

void clicked()
{
    super();
    if(batchRecId)
    {
        qbrBatchNumber.value(SysQuery::value(batchRecId));
        qbrChargeEntryRecid.enabled(false);
        HMPaymentCentreFormHandler.fillChargeEntryLineTmp(batchRecId, HMPatientChargeEntryLinesTmp);
    }
    else
    {
        qbrChargeEntryRecid.enabled(true);
    }
    element.updateBatchAmountDetailFields(batchNumberId);
    HMPatientChargeEntryTable_ds.executeQuery();
    HMPatientChargeEntryLinesTmp_ds.executeQuery();
}

