[Form]
public class HMPharmacy extends FormRun
{
    LogisticsLocationRecId          postalAddressLocation;
    LogisticsLocationRecId          contactInfoLocation;
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = dirPartyPostalAddressView.Location)
{
    postalAddressLocation = _postalAddressLocation;
    return postalAddressLocation;
}

public void initValue()
{
  //  print("dirpartytable initvalue");
    //breakpoint;
    //DirPartyTable.EntityType = DirPartyType::Organization;
    super();
  //  print("dirpartytable initvalue end");
}

public int active()
{
    int ret;

    ret = super();

    if(ret)
    {
        if(HMPharmacy.EPrescriptionID == '')
        {
            IdentificationType.enabled(false);
        }
        else
        {
            IdentificationType.enabled(true);
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(HMPharmacy.EPrescriptionID != '')
    {
        if(!HMPharmacy.HMePrescriptionIdentificationType)
        {
            ret = false;
            warning(strFmt("@SYS84753","@SYS126629"));
        }
    }

    return ret;
}

public void write()
{
    HMPharmacy.Party = DirPartyTable.RecId;
    HMPharmacy.Name = DirPartyTable.Name;
    super();
}

public void modified()
{
    super();

    if(HMPharmacy.EPrescriptionID == '')
    {
        HMPharmacy.HMePrescriptionIdentificationType = 0;
        IdentificationType.enabled(false);
    }
    else
    {
        IdentificationType.enabled(true);
    }
}

public void executeQuery()
{
     DirPartyPostalAddressView::updateQuery(this.query().dataSourceName(this.name()), HMPharmacy.Party);

    super();

    //this.updateButtons();

    if (postalAddressLocation != 0 && postalAddressLocation != dirPartyPostalAddressView.Location)
    {
        this.findValue(fieldnum(DirPartyPostalAddressView,Location),SysQuery::value(postalAddressLocation));
    }
}
