public void displayContacts()
{
    LogisticsElectronicAddress      logisticsElectronicAddress;
    DirPartyLocation                dirPartyLocation;

    select firstOnly * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Phone
            && logisticsElectronicAddress.Description == 'OfficePhone'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    OfficePhone.text(logisticsElectronicAddress.Locator);


    select firstOnly * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Phone
            && logisticsElectronicAddress.Description == 'HomePhone'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    HomePhone.text(logisticsElectronicAddress.Locator);


    select firstOnly * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Phone
            && logisticsElectronicAddress.Description == 'MobilePhone'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    MobilePhone.text(logisticsElectronicAddress.Locator);


    select firstOnly * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Fax
            && logisticsElectronicAddress.Description == 'Fax'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    Fax.text(logisticsElectronicAddress.Locator);


    select firstOnly * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Email
            && logisticsElectronicAddress.Description == 'Email'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    Email.text(logisticsElectronicAddress.Locator);
}

public void init()
{
    super();
    addressController = LogisticsPostalAddressFormHandler::newParameters(HMReferringPhysician_ds, logisticsPostalAddress_ds);
    addressController.callerUpdateQuery(fieldnum(HMReferringPhysician,Location));
}

public int task(int _taskId)
{
    int ret;

    ret = super(_taskId);

    if(_taskId == #taskSave)
    {
        if(DirPerson.RecId)
        {
            element.updateContacts();
            OfficeNumber.update();
        }
    }

    return ret;
}

public void updateContacts()
{
    LogisticsElectronicAddress      logisticsElectronicAddress;
    DirPartyLocation                dirPartyLocation;

    ttsBegin;

    select forUpdate * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Phone
            && logisticsElectronicAddress.Description == 'OfficePhone'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    if(OfficePhone.valueStr() == '')
    {
        logisticsElectronicAddress.delete();
    }
    else if(logisticsElectronicAddress)
    {
        logisticsElectronicAddress.Locator = OfficePhone.valueStr();
        logisticsElectronicAddress.update();
    }
    else
    {
        logisticsElectronicAddress.Description       = 'OfficePhone';
        logisticsElectronicAddress.IsPrimary        = NoYes::No;
        logisticsElectronicAddress.Locator = OfficePhone.valueStr();
        logisticsElectronicAddress.Location = DirPartyLocation::findOrCreate(dirPerson.RecId, logisticsElectronicAddress.Location).Location;
        logisticsElectronicAddress.Type  = LogisticsElectronicAddressMethodType::Phone;
        logisticsElectronicAddress.insert();
    }

    select forUpdate * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Phone
            && logisticsElectronicAddress.Description == 'HomePhone'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    if(HomePhone.valueStr() == '')
    {
        logisticsElectronicAddress.delete();
    }
    else if(logisticsElectronicAddress)
    {
        logisticsElectronicAddress.Locator = HomePhone.valueStr();
        logisticsElectronicAddress.update();
    }
    else
    {
        logisticsElectronicAddress.Description       = 'HomePhone';
        logisticsElectronicAddress.IsPrimary        = NoYes::No;
        logisticsElectronicAddress.Locator = HomePhone.valueStr();
        logisticsElectronicAddress.Location = DirPartyLocation::findOrCreate(dirPerson.RecId, logisticsElectronicAddress.Location).Location;
        logisticsElectronicAddress.Type  = LogisticsElectronicAddressMethodType::Phone;
        logisticsElectronicAddress.insert();
    }

    select forUpdate * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Phone
            && logisticsElectronicAddress.Description == 'MobilePhone'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    if(MobilePhone.valueStr() == '')
    {
        logisticsElectronicAddress.delete();
    }
    else if(logisticsElectronicAddress)
    {
        logisticsElectronicAddress.Locator = MobilePhone.valueStr();
        logisticsElectronicAddress.update();
    }
    else
    {
        logisticsElectronicAddress.Description       = 'MobilePhone';
        logisticsElectronicAddress.IsPrimary        = NoYes::No;
        logisticsElectronicAddress.Locator = MobilePhone.valueStr();
        logisticsElectronicAddress.Location = DirPartyLocation::findOrCreate(dirPerson.RecId, logisticsElectronicAddress.Location).Location;
        logisticsElectronicAddress.Type  = LogisticsElectronicAddressMethodType::Phone;
        logisticsElectronicAddress.insert();
    }

    select forUpdate * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Email
            && logisticsElectronicAddress.Description == 'Email'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    if(Email.valueStr() == '')
    {
        logisticsElectronicAddress.delete();
    }
    else if(logisticsElectronicAddress)
    {
        logisticsElectronicAddress.Locator = Email.valueStr();
        logisticsElectronicAddress.update();
    }
    else
    {
        logisticsElectronicAddress.Description       = 'Email';
        logisticsElectronicAddress.IsPrimary        = NoYes::No;
        logisticsElectronicAddress.Locator = Email.valueStr();
        logisticsElectronicAddress.Location = DirPartyLocation::findOrCreate(dirPerson.RecId, logisticsElectronicAddress.Location).Location;
        logisticsElectronicAddress.Type  = LogisticsElectronicAddressMethodType::Email;
        logisticsElectronicAddress.insert();
    }

    select forUpdate * from logisticsElectronicAddress
        where logisticsElectronicAddress.Type ==  LogisticsElectronicAddressMethodType::Fax
            && logisticsElectronicAddress.Description == 'Fax'
    join * from dirPartyLocation where dirPartyLocation.Location == logisticsElectronicAddress.Location
        && dirPartyLocation.Party == dirPerson.RecId;

    if(Fax.valueStr() == '')
    {
        logisticsElectronicAddress.delete();
    }
    else if(logisticsElectronicAddress)
    {
        logisticsElectronicAddress.Locator = Fax.valueStr();
        logisticsElectronicAddress.update();
    }
    else
    {
        logisticsElectronicAddress.Description       = 'Fax';
        logisticsElectronicAddress.IsPrimary        = NoYes::No;
        logisticsElectronicAddress.Locator = Fax.valueStr();
        logisticsElectronicAddress.Location = DirPartyLocation::findOrCreate(dirPerson.RecId, logisticsElectronicAddress.Location).Location;
        logisticsElectronicAddress.Type  = LogisticsElectronicAddressMethodType::Fax;
        logisticsElectronicAddress.insert();
    }

    ttsCommit;
}

