public boolean validateWrite()
{
    boolean ret;
    str     personName;

    if(!(DirPersonName.FirstName ==''))
        personName = DirPersonName.FirstName;
    if(!(DirPersonName.MiddleName ==''))
        personName += ' ' + DirPersonName.MiddleName;
    if(!(DirPersonName.LastName ==''))
        personName += ' ' + DirPersonName.LastName;

    DirPerson.Name = personName;

    ret = super();

    return ret;
}

void clicked()
{
    super();

    if(DirPerson.RecId)
    {
        element.updateContacts();
        OfficeNumber.update();
    }
}
