[Form]
public class HMScannedImageViewer extends FormRun
{
    #define.JPEG('.JPEG')
    CustTable               custTable;
    HcmWorker               hcmWorker;
    System.Drawing.Image    Img;
    DocuType                docuType;
}

public boolean checkValidations()
{
    #define.fileType('JPEG')
    DocuFileTypes   docuFileTypes;
    boolean ret = true;

    docuType  = DocuType::find(HMParameters::getMedicalDocumentsDocuTypeId());

    if (strLRTrim(Name.valueStr()) == "")
    {
        ret = checkFailed("@SYS95848");
    }

    if (ret && docuFileTypes.isEnabled(#fileType))
    {
        ret = checkFailed("@MAZ1317");
    }

    if (ret && !docuType.RecId)
    {
        ret = checkFailed("@MAZ6245");
    }

    return ret;
}

public void init()
{
    if(element.args().parm() == 'Worker')
    {
        hcmWorker = HcmWorker::find(element.args().record().RecId);
    }
    else if(element.args().parm() == 'Customer')
    {
        custTable = CustTable::findRecId(element.args().record().RecId);
    }

    super();
}

public void run()
{
    super();
    //todo: ax7 cannot set  Img = element.args().parmObject();  element.args().parmObject() is object type and img is image
    //Img = element.args().parmObject();
    element.showImage(Img);
}

