public void write()
{
    DirPartyTable party;

    //super();
    if(CustTable.isNewRecord())
    {
        CustTable.CustType = CustType::TPA;
        CustTable.insert(DirPartyType::Organization);
    }
    else
    {
        CustTable.update();
    }

    ttsBegin;
    select forUpdate party where party.RecId == CustTable.Party;
    party.Name = Hmtpa.Name;
    party.update();
    ttsCommit;
}

