public void lookup(FormControl _formControl, str _filterStr)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource,qbdsAdmName;
    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(PriceDiscAdmTable), _formControl);
    sysTableLookup.addLookupField(fieldNum(PriceDiscAdmTable, JournalNum));
    sysTableLookup.addLookupField(fieldNum(PriceDiscAdmTable, JournalName));
    sysTableLookup.addLookupField(fieldNum(PriceDiscAdmTable, Name));
    queryBuildDataSource = query.addDataSource(tableNum(PriceDiscAdmTable));
    qbdsAdmName = queryBuildDataSource.addDataSource(tableNum(PriceDiscAdmName));
    qbdsAdmName.relations(true);
    qbdsAdmName.addRange(fieldNum(PriceDiscAdmName,DefaultRelation)).value(SysQuery::value(PriceType::PriceSales));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(PriceDiscAdmTable, Posted));
    queryBuildRange.value(enum2str(NoYes::Yes));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void write()
{
    CustTable            _custtable;
    HMInsuranceCarrier   _HMInsuranceCarrier  ;
    super();

    ttsBegin;
    while select * from _HMInsuranceCarrier where _HMInsuranceCarrier.recid == HMTpaDetails_ds.object(fieldNum(HMTpaDetails,HMInsuranceCarrier)).getValue()
    join forUpdate _custtable
    where _custtable.AccountNum== _HMInsuranceCarrier.Code
    {
        _custtable.InvoiceAccount = HMTPA.Code;
        _custtable.update();
    }
    ttsCommit;

}

public boolean validateWrite()
{
    HMTpaDetails  HmTpaDetails_local;
    boolean ret;
    select *from HmTpaDetails_local where HmTpaDetails_local.HMInsuranceCarrier == HMTpaDetails_ds.object(fieldNum(HMTpaDetails,HMInsuranceCarrier)).getValue();
    if(HmTpaDetails_local.HMInsuranceCarrier)
    {
        throw error ("@MAZ13464");
    }
    else
    {
        ret = super();
    }

    return ret;
}

