public void pageActivated()
{
    super();

    if (!isPowerBITabPageInitialized)
    {
        str resourceName = resourceStr(HMClaimManagerWorkspacePBIX);

        PowerBIReportSetupHelper helper = PowerBIReportSetupHelper::construct();
        helper.parmIsCrossCompany(false);
        helper.parmGroupControl(PowerBIGroup);
        helper.parmResourceName(ResourceName);
        helper.run();

        isPowerBITabPageInitialized = true;
    }
}
