/// <summary>
/// Populates the data on the temp table that is used as a datasource on this form and sums the aging buckets
/// from the current user selected bucket to the most overdue bucket
/// </summary>
public void buildCustAgingListGridOverDueAmountTmp()
{
    CustCollectionsAgedBalancesChartData agedBalancesChartData;
    StatRepIntervalLine statRepIntervalLineNumLookup;
    CustAging custAgingLocal;
    CustTable customerTable;

    delete_from custAgingListGridOverDueAmountTmp;

    CustVendReportName localSelectedAgingPeriod = element.parmSelectedAgingPeriodDef();

    SelectableDataArea localCompany = element.parmCompany();

    changecompany(localCompany)
    {
        select firstonly LineNum from statRepIntervalLineNumLookup
            where statRepIntervalLineNumLookup.RecId == element.parmCurrentAgingBucket() &&
                  statRepIntervalLineNumLookup.Name == localSelectedAgingPeriod;

        LineNum statRepLine = statRepIntervalLineNumLookup.LineNum;

        insert_recordset custAgingListGridOverDueAmountTmp(OverDueAmount, OverDueAmountMSTSecond, AccountNum, StatRepInterval, CompanyContext)
            select sum(OpenBalanceMST), sum(OpenBalanceMstSecondary), CustTable, localSelectedAgingPeriod, localCompany from agedBalancesChartData
                group by CustTable
                where agedBalancesChartData.StatRepIntervalLineNum <= statRepLine &&
                      agedBalancesChartData.StatRepInterval == localSelectedAgingPeriod
            exists join customerTable
                where customerTable.AccountNum == agedBalancesChartData.CustTable
                    && customerTable.CustType == CustType::Insurance;;
    }

}

public void onFilterChanged()
{
    element.updateCurrentPoolFilters(CustCollectionsWorkspaceHelper::updateFormPartFiltersFromCallerWorkspace(element.parmCaller(), element as CustCollectionsIWorkspaceFormPart));

    element.setTouched();
}

public void init()
{
    super();

    if (!element.args() || !element.args().caller() || !(element.args().caller() is CustCollectionsICollectionManagerWorkspace))
    {
        throw error("@AccountsReceivable:ErrorExpectedCallerArgs");
    }

    if (element.args().caller().name() == formStr(CustomerCollectionManagerWorkspace))
    {
        //Setting the caller form to retrieve the currency type selected; either accounting or reporting from workspace form.
        element.parmCallerForm(element.args().caller());            
    }

    element.parmCaller(element.args().caller() as CustCollectionsICollectionManagerWorkspace);

    agingIndicators = new Array(Types::Enum);
    listPageHelper = CustCollectionsCrossCompanyListPageHelper::construct(poolFilter);

    element.updateCurrentPoolFilters(CustCollectionsWorkspaceHelper::updateFormPartFiltersFromCallerWorkspace(element.parmCaller(), element as CustCollectionsIWorkspaceFormPart));

    custAgingListGridOverDueAmount.linkPhysicalTableInstance(custAgingListGridOverDueAmountTmp);
}

/// <summary>
/// Updates the current pool filters to the current company and aging bucket if either parameter is true
/// then the filters will be defaulted
/// </summary>
/// <param name = "_defaultPoolValues">True if the pool filter values should be defaulted</param>
/// <param name = "_defaultAgingBucket">True if the aging bucket should be defaulted</param>
public void updateCurrentPoolFilters(boolean _defaultPoolValues, boolean _defaultAgingBucket = false)
{
    if (_defaultPoolValues)
    {
        listPageHelper.updateCompany(element.parmCompany());
        listPageHelper.loadPools();

        listPageHelper.checkForAging();

        element.parmSelectedAgingPeriodDef(listPageHelper.getDefaultAgingPeriod());

        poolFilter.enabled(listPageHelper.getCurrentAgent().RecId != 0);

        poolFilter.text(listPageHelper.getInitialFilterValue(''));
    }

    if (_defaultAgingBucket || _defaultPoolValues)
    {
        StatRepIntervalLine intervalLines;

        select firstonly crosscompany RecId, Heading
            from intervalLines
            order by intervalLines.LineNum
            where intervalLines.Name == element.parmSelectedAgingPeriodDef() &&
                  intervalLines.DataAreaId == element.parmCompany();

        agingBucketFilter.enabled(intervalLines.RecId != 0);
        element.parmCurrentAgingBucket(intervalLines.RecId);
        agingBucketFilter.text(intervalLines.Heading);
    }

    agingPeriodFilter.text(element.parmSelectedAgingPeriodDef());
    agingPeriodFilter.enabled(element.parmSelectedAgingPeriodDef() != '');

    if (element.parmCallerForm())
    {
        currencySelected = element.parmCallerForm().parmCurrencySelection();
    }

    if (currencySelected == LedgerCurrencySelection::Accounting)
    {
        CustAging_AmountDueMst.visible(true);
        CustAging_AmountDueMstSecondary.visible(false);
        OverDueAmount.visible(true);
        OverDueAmountMSTSecond.visible(false);
    }
    else
    {
        CustAging_AmountDueMst.visible(false);
        CustAging_AmountDueMstSecondary.visible(true);
        OverDueAmount.visible(false);
        OverDueAmountMSTSecond.visible(true);
    }

    CustCollectionsWorkspaceHelper::togglePoolFilterForCustomerFilter(element, listPageHelper, poolFilter);

}

/// <summary>
/// Collects the information to email a statement to a customer whent when the 'Email statement to contact' drop down is opened
/// </summary>
/// <param name = "_agedBy">Transaction due date</param>
/// <param name = "_agingDate">Date aged on</param>
/// <param name = "_includeSettled">Include settled trans actions</param>
/// <param name = "_settledTransStartDate">Start date for the settled transactions</param>
public void sendEmailToContactWithStatement(DateTransactionDuedate _agedBy, CustAgingDate _agingDate, boolean _includeSettled, TransDate _settledTransStartDate)
{
    CustCollectionsWorkspaceHelper::changeCompanyContext(element.parmCompany());

    ContactPerson contactPersonCollections = custTable.getCollectionsContactPerson();
    CustCollectionsWorkspaceHelper::sendEmailWithAttachment(
            this,
            element.getContactEmail(contactPersonCollections),
            CustCollectionsEmailType::ContactWithStatement,
            menuitemActionStr(CustCollectionsEmailContactStatement),
            custTable,
            [element.parmCompany()],
            contactPersonCollections.personName(),
            element.parmSelectedAgingPeriodDef(),
            _agedBy,
            _agingDate,
            _includeSettled,
            _settledTransStartDate);
}

