/// <summary>
/// Shows the aging indicator icon for the bucket
/// </summary>
/// <param name = "_custTable">The <c>CustTable</c> record</param>
/// <returns>Container with the aging icon</returns>
public display container showAgingIndicator(CustTable _custTable)
{
    ImageReference imgRef = ImageReference::constructForSymbol('');

    int periodCount = agingIndicators.lastIndex();

    CustAgingLinePosition nonZeroBalanceAgingPeriod = CustCollectionsWorkspaceHelper::getFirstNonZeroBalanceAgingPeriod(_custTable, element.parmSelectedAgingPeriodDef(), element.parmCompany());

    if (nonZeroBalanceAgingPeriod && nonZeroBalanceAgingPeriod <= periodCount)
    {
        // Set the icon appropriately
        imgRef = ImageReference::constructForSymbol(StatRepIntervalLine::getResourceIdForAgingIndicator(
            agingIndicators.value(nonZeroBalanceAgingPeriod)),
            enum2Str(agingIndicators.value(nonZeroBalanceAgingPeriod)));
    }

    return imgRef.pack();
}

public void executeQuery()
{
    agingIndicators = CustCollectionsWorkspaceHelper::populateAgingIndicators(element.parmSelectedAgingPeriodDef(), element.parmCompany());

    boolean poolSelected = strLen(poolFilter.valueStr()) != 0;

    if (poolSelected)
    {
        this.query(CustCollectionsWorkspaceHelper::addOriginalPoolQuery(listPageHelper,
            element.parmSelectedAgingPeriodDef(),
            custAging1_DS.name(),
            custAgingLegalEntity1_DS.name(),
            dirPartyTableContact_DS.name(),
            dirPartyTable_DS.name()));
    }
    else
    {
        Query q = CustCollectionsPool::defaultQuery(CustPoolType::Collection);

        q.allowCrossCompany(listPageHelper.isCrossCompany());

        CustCollectionsWorkspaceHelper::buildCustAgingQuery(q,
            element.parmSelectedAgingPeriodDef(),
            custAging1_DS.name(),
            custAgingLegalEntity1_DS.name(),
            dirPartyTableContact_DS.name(),
            dirPartyTable_DS.name());

        this.query(q);
    }

    QueryBuildDataSource custAgingListGridOverDueAmountDS = this.query().dataSourceTable(tableNum(CustTable)).addDataSource(tableNum(CustAgingListGridOverDueAmountTmp), CustAgingListGridOverDueAmount_ds.name());

    custAgingListGridOverDueAmountDS.addRange(fieldNum(CustAgingListGridOverDueAmountTmp, OverDueAmount)).value(SysQuery::valueNot(0));
    custAgingListGridOverDueAmountDS.addLink(fieldNum(CustTable, AccountNum), fieldNum(CustAgingListGridOverDueAmountTmp, AccountNum));
    custAgingListGridOverDueAmountDS.addLink(fieldNum(CustTable, DataAreaId), fieldNum(CustAgingListGridOverDueAmountTmp, CompanyContext));
    custAgingListGridOverDueAmountDS.joinMode(JoinMode::InnerJoin);

    CustCollectionsWorkspaceHelper::addCallerCompanyFilter(this, element.parmCompany());

    CustCollectionsWorkspaceHelper::applyCustAccountFilter(listPageHelper, this, fieldNum(CustTable, AccountNum), element.parmCompany(), poolSelected, element.parmCustAccountFilter());

    element.buildCustAgingListGridOverDueAmountTmp();

    super();
}

public void setTouched()
{
    this.executeQuery();
}

