{
    Args args = new Args(agingBucketFilter);
    args.name(formstr(CustCollectionsAgingBucketLookup));

    Object formRun = classfactory.formRunClass(args);
    formRun.custCollectionAgingBuckets(CustCollectionsWorkspaceHelper::getAgingBuckets(listPageHelper, element.parmSelectedAgingPeriodDef(), element.parmCompany()));
    formRun.init();

    formRun.selectRecord(element.parmCurrentAgingBucket());

    agingBucketFilter.performFormLookup(formRun);
}

