public void init()
{
    super();

    if (!element.args() || !element.args().caller() || !(element.args().caller() is CustCollectionsICollectionManagerWorkspace))
    {
        throw error("@AccountsReceivable:ErrorExpectedCallerArgs");
    }

    if (element.args().caller().name() == formStr(mzkClaimManagerWorkspace))
    {
        //Setting the caller form to retrieve the currency type selected; either accounting or reporting from workspace form.
        element.parmCallerForm(element.args().caller());            
    }

    element.parmCaller(element.args().caller() as CustCollectionsICollectionManagerWorkspace);

    agingIndicators = new Array(Types::Enum);

    listPageHelper = CustCollectionsCrossCompanyListPageHelper::construct(poolFilter);

    element.updateCurrentPoolFilters(mzkCustCollectionsWorkspaceHelper::updateFormPartFiltersFromCallerWorkspace(element.parmCaller(), element));
}

/// <summary>
/// Updates the pool filter based on the selected company and defaults the values
/// </summary>
/// <param name = "_default">If the values should be defaulted</param>
public void updateCurrentPoolFilters(boolean _default)
{
    if (_default)
    {
        listPageHelper.updateCompany(element.parmCompany());
        listPageHelper.loadPools();

        poolFilter.text(listPageHelper.getInitialFilterValue(''));

        listPageHelper.checkForAging();

        element.parmSelectedAgingPeriodDef(listPageHelper.getDefaultAgingPeriod());
    }

    agingPeriodFilter.text(element.parmSelectedAgingPeriodDef());
    agingPeriodFilter.enabled(element.parmSelectedAgingPeriodDef() != '');

    poolFilter.enabled(listPageHelper.getCurrentAgent().RecId != 0);

    if (element.parmCallerForm())
    {
        currencySelected = element.parmCallerForm().parmCurrencySelection();
    }

    if (currencySelected == LedgerCurrencySelection::Accounting)
    {
        CustAging_AmountDueMst.visible(true);
        CustAging_AmountDueMstSecondary.visible(false);
        CustAgingLine1_OpenBalanceMst.visible(true);
        CustAgingLine2_OpenBalanceMst.visible(true);
        CustAgingLine3_OpenBalanceMst.visible(true);
        CustAgingLine4_OpenBalanceMst.visible(true);
        CustAgingLine5_OpenBalanceMst.visible(true);
        CustAgingLine6_OpenBalanceMst.visible(true);
        CustAging1_OpenBalanceMSTSecondary.visible(false);
        CustAging2_OpenBalanceMSTSecondary.visible(false);
        CustAging3_OpenBalanceMSTSecondary.visible(false);
        CustAging4_OpenBalanceMSTSecondary.visible(false);
        CustAging5_OpenBalanceMSTSecondary.visible(false);
        CustAging6_OpenBalanceMSTSecondary.visible(false);
    }
    else
    {
        CustAging_AmountDueMst.visible(false);
        CustAging_AmountDueMstSecondary.visible(true);
        CustAgingLine1_OpenBalanceMst.visible(false);
        CustAgingLine2_OpenBalanceMst.visible(false);
        CustAgingLine3_OpenBalanceMst.visible(false);
        CustAgingLine4_OpenBalanceMst.visible(false);
        CustAgingLine5_OpenBalanceMst.visible(false);
        CustAgingLine6_OpenBalanceMst.visible(false);
        CustAging1_OpenBalanceMSTSecondary.visible(true);
        CustAging2_OpenBalanceMSTSecondary.visible(true);
        CustAging3_OpenBalanceMSTSecondary.visible(true);
        CustAging4_OpenBalanceMSTSecondary.visible(true);
        CustAging5_OpenBalanceMSTSecondary.visible(true);
        CustAging6_OpenBalanceMSTSecondary.visible(true);           
    }

    mzkCustCollectionsWorkspaceHelper::togglePoolFilterForCustomerFilter(element, listPageHelper, poolFilter);
}

