/// <summary>
/// Changes the text in the aging period balance columns or hides them as necessary.
/// </summary>
/// <remarks>
/// This method hides columns that are not necessary. The user could
/// display them by adding them back in and those columns would have the previous text
/// in them. The values under them would all be zero.
/// </remarks>
public void setGridColumnLabels()
{
    StatRepIntervalLine intervalLines;
    Array columns = this.getAgingBucketColumnsForGrid();
    Array reportingColumns = this.getReportingCurrencyAgingBucketColumnsForGrid();
    int lineNumber = 1;

    if(listPageHelper.checkAgingPeriodValid(element.parmSelectedAgingPeriodDef()))
    {
        while select crosscompany Heading from intervalLines
            order by intervalLines.LineNum desc
            where intervalLines.Name == element.parmSelectedAgingPeriodDef() &&
                  intervalLines.DataAreaId == element.parmCompany()
        {
            if (strLen(intervalLines.Heading) > 0)
            {
                columns.value(lineNumber).showLabel(true);
                columns.value(lineNumber).label(intervalLines.Heading);

                reportingColumns.value(lineNumber).showLabel(true);
                reportingColumns.value(lineNumber).label(intervalLines.Heading);
            }
            else
            {
                columns.value(lineNumber).showLabel(false);
                reportingColumns.value(lineNumber).showLabel(false);
            }              

            if (currencySelected == LedgerCurrencySelection::Accounting)
            {
                columns.value(lineNumber).visible(true);
            }
            else
            {
                reportingColumns.value(lineNumber).visible(true);
            }

            lineNumber++;
        }
    }

    Counter numberOfAgedBuckets = CustVendBalanceList::numOfBalanceAge();
    while (lineNumber < numberOfAgedBuckets)
    {
        columns.value(lineNumber).visible(false);
        reportingColumns.value(lineNumber).visible(false);

        lineNumber++;
    }
}

