/// <summary>
/// Collects the information to email a statement to a customer whent when the 'Email statement to contact' drop down is opened
/// </summary>
/// <param name = "_agedBy">Transaction due date</param>
/// <param name = "_agingDate">Date aged on</param>
/// <param name = "_includeSettled">Include settled trans actions</param>
/// <param name = "_settledTransStartDate">Start date for the settled transactions</param>
public void sendEmailToContactWithStatement(DateTransactionDuedate _agedBy, CustAgingDate _agingDate, boolean _includeSettled, TransDate _settledTransStartDate)
{
    mzkCustCollectionsWorkspaceHelper::changeCompanyContext(element.parmCompany());

    ContactPerson contactPersonCollections = custTable.getCollectionsContactPerson();
    mzkCustCollectionsWorkspaceHelper::sendEmailWithAttachment(
            this,
            element.getContactEmail(contactPersonCollections),
            CustCollectionsEmailType::ContactWithStatement,
            menuitemActionStr(CustCollectionsEmailContactStatement),
            custTable,
            [element.parmCompany()],
            contactPersonCollections.personName(),
            element.parmSelectedAgingPeriodDef(),
            _agedBy,
            _agingDate,
            _includeSettled,
            _settledTransStartDate);
}

