public display container showAgingIndicator(CustTable _custTable)
{
    ImageReference imgRef = ImageReference::constructForSymbol('');

    int periodCount = agingIndicators.lastIndex();

    CustAgingLinePosition nonZeroBalanceAgingPeriod = mzkCustCollectionsWorkspaceHelper::getFirstNonZeroBalanceAgingPeriod(_custTable, element.parmSelectedAgingPeriodDef(), element.parmCompany());

    if (nonZeroBalanceAgingPeriod && nonZeroBalanceAgingPeriod <= periodCount)
    {
        // Set the icon appropriately
        imgRef = ImageReference::constructForSymbol(StatRepIntervalLine::getResourceIdForAgingIndicator(agingIndicators.value(nonZeroBalanceAgingPeriod)),
            enum2str(agingIndicators.value(nonZeroBalanceAgingPeriod)));
    }

    return imgRef.pack();
}

public void executeQuery()
{
    agingIndicators = mzkCustCollectionsWorkspaceHelper::populateAgingIndicators(element.parmSelectedAgingPeriodDef(), element.parmCompany());

    boolean poolSelected = strLen(poolFilter.valueStr()) != 0;
    if (poolSelected)
    {
        this.query(mzkCustCollectionsWorkspaceHelper::addOriginalPoolQuery(listPageHelper,
            element.parmSelectedAgingPeriodDef(),
            mzkcustAging1_DS.name(),
            mzkcustAgingLegalEntity1_DS.name(),
            dirPartyTableContact_DS.name(),
            dirPartyTable_DS.name()));
    }
    else
    {
        Query q = CustCollectionsPool::defaultQuery(CustPoolType::Collection);

        q.allowCrossCompany(listPageHelper.isCrossCompany());

        mzkCustCollectionsWorkspaceHelper::buildCustAgingQuery(q,
            element.parmSelectedAgingPeriodDef(),
            mzkcustAging1_DS.name(),
            mzkcustAgingLegalEntity1_DS.name(),
            dirPartyTableContact_DS.name(),
            dirPartyTable_DS.name());

        this.query(q);
    }

    mzkCustCollectionsWorkspaceHelper::applyCustAccountFilter(listPageHelper, this, fieldNum(CustTable, AccountNum), element.parmCompany(), poolSelected, element.parmCustAccountFilter());

    mzkCustCollectionsWorkspaceHelper::addCallerCompanyFilter(this, element.parmCompany());

    this.query().dataSourceTable(tableNum(CustTable)).addRange(fieldNum(CustTable, custType)).value(SysQuery::value(CustType::Insurance));

    super();

    element.setGridColumnLabels();
}

public void setTouched()
{
    this.executeQuery();
}

