public boolean modified()
{
    boolean ret = super();

    if (ret && strLen(this.text()) && !listPageHelper.checkPoolExists(this.text()))
    {
        ret = checkFailed("@AccountsReceivable:CustCollectionsPoolDoesNotExistsError");
    }

    if (ret)
    {
        element.setTouched();
        agingGrid.setFocus();
    }

    return ret;
}

public void lookup()
{
    SysTableLookup lookup = SysTableLookup::newParameters(tableNum(StatRepInterval), this, true);

    lookup.addLookupfield(fieldNum(StatRepInterval, Name), true);
    lookup.addLookupfield(fieldNum(StatRepInterval, Description));

    str statRepIntervalFilter;

    StatRepInterval statRep;
    while select crosscompany statRep
        where statRep.DataAreaId == element.parmCompany()
    {
        if (StatRepIntervalLine::countLinesInCompany(statRep.Name, element.parmCompany()) <= (CustVendBalanceList::numOfBalanceAge() - 1))
        {
            if (statRepIntervalFilter == '')
            {
                statRepIntervalFilter = queryValue(statRep.Name);
            }
            else
            {
                statRepIntervalFilter = strFmt('%1,%2', statRepIntervalFilter, queryValue(statRep.Name));
            }
        }
    }

    Query q = new Query();
    q.allowCrossCompany(true);
    QueryBuildDataSource statRepDS = q.addDataSource(tableNum(StatRepInterval));
    q.addCompanyRange(element.parmCompany());
    statRepDS.addRange(fieldNum(StatRepInterval, Name)).value(strFmt('=%1', statRepIntervalFilter));

    lookup.parmQuery(q);
    lookup.performFormLookup();
}

public boolean modified()
