Method.Source:
/// <summary>
///    Determines whether the specified record is located in the <c>HMAmendmentSource</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMAmendmentSource</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMAmendmentSource</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMAmendmentSource::exist(_recId))
    {
        ret = checkFailed(strfmt(HMAmendmentSource::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMAmendmentSource</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMAmendmentSource</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMAmendmentSource
             where
                 HMAmendmentSource.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMAmendmentSource</c> table.
/// </summary>
/// <param name="_recid">
///    The record ID of the <c>HMAmendmentSource</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMAmendmentSource</c> table; otherwise, an empty record.
/// </returns>
public static HMAmendmentSource find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMAmendmentSource   hmAmendmentSource;

    hmAmendmentSource.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmAmendmentSource.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmAmendmentSource
        where hmAmendmentSource.RecId == _recid;

    return hmAmendmentSource;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMAmendmentSource</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
