public void insert()
{
    if(this.TransDate == dateNull())
    {
        this.TransDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
    }
    super();
}

public static RecId createCasePaymtReceived(RecId _hmcase, real _amountPaid, real _returnedAmount,
                                            //Zain
                                            PaymNote    _paymNotes,
                                            PaymReference   _paymReference,
                                            PaymMode _custPaymMode,
                                            RecId _ledgerJournalTransId = 0)
                                            //Zain
{
    HMCasePaymtReceivedDetails paymt;

    paymt.initValue();
    paymt.HMCase = _hmcase;
    paymt.AmountPaid = _amountPaid;
    //Zain
    paymt.CustPaymModeTable = _custPaymMode;
    paymt.PaymReference = _paymReference;
    paymt.PaymentNotes = _paymNotes;
    paymt.LedgerJournalTrans = _ledgerJournalTransId;
    //Zain
    paymt.ReturnedAmount = _returnedAmount;
    paymt.insert();
    return paymt.RecId;
}

/// <summary>
/// Finds the specified record in the <c>HMCasePaymtReceivedDetails</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the <c>HMCasePaymtReceivedDetails</c> record to find.
/// </param>
/// <param name="_forupdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when it reads the record; optional.
/// </param>
/// <returns>
/// A record in the <c>HMCasePaymtReceivedDetails</c> table; otherwise, an empty record.
/// </returns>
public static HMCasePaymtReceivedDetails find(
    RecId _recId,
    boolean _forupdate = false,
    ConcurrencyModel _concurrencyModel = ConcurrencyModel::Auto)
{
    HMCasePaymtReceivedDetails CasePaymtReceivedDetails;

    if (_recId)
    {
        CasePaymtReceivedDetails.selectForUpdate(_forupdate);

        if (_forupdate  && _concurrencyModel != ConcurrencyModel::Auto)
        {
            CasePaymtReceivedDetails.concurrencyModel(_concurrencyModel);
        }

        select firstonly
                CasePaymtReceivedDetails
            where
                CasePaymtReceivedDetails.RecId == _recId;
    }

    return CasePaymtReceivedDetails;
}

/// <summary>
///
/// </summary>
[SysClientCacheDataMethodAttribute]
public display Description getPaymentLineStatus()
{
    // Musab - Start:
    if (this.IsPaymtCancelled == NoYes::Yes)
    {
        return enum2Str(HMPatientPaymentLineStatus::Cancelled);
    }
    // End.
    else if (this.LedgerJournalTrans)
    {
        return enum2Str(HMPatientPaymentLineStatus::Posted);
    }
    else
    {
        return enum2Str(HMPatientPaymentLineStatus::NotPosted);
    }
}
