display Amount displayBalance()
{
    return this.displayInvoiceAmount() - this.displayPaymentAmount() - this.displayWriteOffAmount();
}

display Amount displayInvoiceAmount()
{
    HMCaseTrackingLine  hmCaseTrackingLine;

    select sum(Amount) from HMCaseTrackingLine
        where HMCaseTrackingLine.HMCaseTrackingHeader == this.RecId
        && HMCaseTrackingLine.TransactionType == HMCaseTrackingLineTransactionType::Invoice;

    return HMCaseTrackingLine.Amount;
}

display Amount displayPaymentAmount()
{
    HMCaseTrackingLine  hmCaseTrackingLine;

    select sum(Amount) from HMCaseTrackingLine
        where HMCaseTrackingLine.HMCaseTrackingHeader == this.RecId
        && HMCaseTrackingLine.TransactionType == HMCaseTrackingLineTransactionType::PaymentReceived;

    return HMCaseTrackingLine.Amount;
}

display Amount displayWriteOffAmount()
{
    HMCaseTrackingLine  hmCaseTrackingLine;

    select sum(Amount) from HMCaseTrackingLine
        where HMCaseTrackingLine.HMCaseTrackingHeader == this.RecId
        && HMCaseTrackingLine.TransactionType == HMCaseTrackingLineTransactionType::Writeoff;

    return HMCaseTrackingLine.Amount;
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMCaseTrackingHeader</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMCaseTrackingHeader</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMCaseTrackingHeader</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMCaseTrackingHeader::exist(_recId))
    {
        ret = checkFailed(strfmt(HMCaseTrackingHeader::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMCaseTrackingHeader</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMCaseTrackingHeader</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMCaseTrackingHeader
             where
                 HMCaseTrackingHeader.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMCaseTrackingHeader</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMCaseTrackingHeader</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMCaseTrackingHeader</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMCaseTrackingHeader find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMCaseTrackingHeader  HMCaseTrackingHeader;

    HMCaseTrackingHeader.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMCaseTrackingHeader.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from HMCaseTrackingHeader where HMCaseTrackingHeader.RecId == _recid;

    return HMCaseTrackingHeader;
}

public static HMCaseTrackingHeader findbyAppointment(
RecId               _recAppointmentid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMCaseTrackingHeader  HMCaseTrackingHeader;

    HMCaseTrackingHeader.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMCaseTrackingHeader.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from HMCaseTrackingHeader where HMCaseTrackingHeader.HMResourceSchedulingAppointment == _recAppointmentid;

    return HMCaseTrackingHeader;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMCaseTrackingHeader</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
