public static RecId createCaseTransDetail(RecId _hmCaseTrans,
                                    HMCaseTransDetailTransactionType _transType,
                                    RecId _refRecId,
                                    TableId _refTableId,
                                    real _value = 0,
                                    RecId _paymentReceivedRecId = 0)
{
    HMCaseTransDetail hmCaseTransDetail;
    try
    {
        hmCaseTransDetail.HMCaseTrans = _hmCaseTrans;
        hmCaseTransDetail.TransactionType = _transType;
        hmCaseTransDetail.RefRecId = _refRecId;
        hmCaseTransDetail.RefTableId = _refTableId;
        hmCaseTransDetail.VAlue = _value;
        hmCaseTransDetail.HMCasePaymtReceivedDetails = _paymentReceivedRecId;
        hmCaseTransDetail.insert();
        return hmCaseTransDetail.RecId;
    }
    catch
    {
        return 0;
    }

}

