public TableName getTableNameFromRef(RefRecId _recId)
{
    switch(_recId)
    {
        case tableNum(CustTrans): return tableStr(CustTrans);  break;
        case tableNum(CustTable): return tableStr(CustTable);  break;
        case tableNum(LedgerJournalTrans): return tableStr(LedgerJournalTrans);  break;
        case tableNum(CustInvoiceLine): return tableStr(CustInvoiceLine);  break;
        case tableNum(InventJournalTrans): return tableStr(InventJournalTrans);  break;
        default: return "";
    }
}
