/// <summary>
///
/// </summary>
public static RecId insertData(HMClaimJobType _jobType
    , RefRecId _batchJobId
    , int64 _numberOfQualifiedClaims
    , int64 _numberOfThreads)
{
    HMClaimBatchJobLogsHeader logs;

    logs.JobType                    = _jobType;
    logs.BatchJobId                 = _batchJobId;
    logs.NumberOfQualifiedClaims    = _numberOfQualifiedClaims;
    logs.NumberOfThreads            = _numberOfThreads;
    logs.insert();

    return logs.RecId;
}

/// <summary>
///
/// </summary>
public display int64 NumberOfProcessedClaims()
{
    HMClaimBatchJobsLogs hmClaimBatchJobsLogs;

    select count(RecId) from hmClaimBatchJobsLogs
        where hmClaimBatchJobsLogs.JobType == this.JobType
            && hmClaimBatchJobsLogs.HMClaimBatchJobLogsHeader == this.RecId
            && hmClaimBatchJobsLogs.ClaimProcessingStatus == HMClaimProcessingStatus::Successful;

    return hmClaimBatchJobsLogs.RecId;
}

/// <summary>
///
/// </summary>
public display int64 NumberOfFailedClaims()
{
    HMClaimBatchJobsLogs hmClaimBatchJobsLogs;

    select count(RecId) from hmClaimBatchJobsLogs
        where hmClaimBatchJobsLogs.JobType == this.JobType
            && hmClaimBatchJobsLogs.HMClaimBatchJobLogsHeader == this.RecId
            && hmClaimBatchJobsLogs.ClaimProcessingStatus == HMClaimProcessingStatus::Failed;


    return hmClaimBatchJobsLogs.RecId;
}

public display int64 NumberOfUnprocessedClaims()
{
    HMClaimBatchJobsLogs hmClaimBatchJobsLogs;

    select count(RecId) from hmClaimBatchJobsLogs
        where hmClaimBatchJobsLogs.JobType == this.JobType
            && hmClaimBatchJobsLogs.HMClaimBatchJobLogsHeader == this.RecId
            && hmClaimBatchJobsLogs.ClaimProcessingStatus == HMClaimProcessingStatus::Unprocessed;

    return hmClaimBatchJobsLogs.RecId;
}
