/// <summary>
///    Determines whether the specified record is located in the <c>HMHealthMaintenanceAction</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMHealthMaintenanceAction</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMClaimReasonTable</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMClaimReasonTable::exist(_recId))
    {
        ret = checkFailed(strfmt(HMClaimReasonTable::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMHealthMaintenanceAction</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMClaimReasonTable</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMClaimReasonTable
             where
                 HMClaimReasonTable.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///     Finds the specified record in the <c>HMClaimReasonTable</c> table.
/// </summary>
/// <param name="_recid">
///     The record ID of the <c>HMClaimReasonTable</c> table to find.
/// </param>
/// <param name="_forUpdate">
///     A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///     The concurrency model option that will be used when it reads the record; optional.
/// </param>
/// <returns>
///     A record of the <c>HMClaimReasonTable</c> table if found; otherwise, an empty buffer.
/// </returns>
public static HMClaimReasonTable find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClaimReasonTable  hmClaimReasonTable;
    hmClaimReasonTable.selectForUpdate(_forUpdate);

    if(_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmClaimReasonTable.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmClaimReasonTable where hmClaimReasonTable.RecId == _recid;

    return hmClaimReasonTable;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMClaimReasonTable</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
