/// <summary>
/// Validates that the specified record exists in the <c>HMClaimSchema</c> table; if not, an error message is displayed.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean checkExist(recId _recId)
{
    boolean ret = true;

    if (!HMClaimSchema::exist(_recId))
    {
        ret = checkFailed(strfmt(HMClaimSchema::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
/// Indicates whether the specified record in the <c>HMClaimSchema</c> table exists.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(recId _recId)
{
    boolean exist;

    exist = (select firstonly
                RecId
            from
                hmClaimSchema
            where
                hmClaimSchema.RecId == _recId).RecId != 0;

    return exist;
}

/// <summary>
/// Finds the specified record in the <c>HMClaimSchema</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
/// A record of the <c>HMClaimSchema</c> table; otherwise, an empty record.
/// </returns>
public static HMClaimSchema find(
    recId   _recId,
    boolean _forUpdate = false,
    ConcurrencyModel        _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClaimSchema    hmClaimSchema;

    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmClaimSchema.concurrencyModel(_concurrencyModel);
    }

    if (_recId)
    {
        hmClaimSchema.selectForUpdate(_forUpdate);

        select firstonly hmClaimSchema
            where hmClaimSchema.RecId == _recId;
    }

    return hmClaimSchema;
}
