edit TransDate firstIllnesDateEdit(Boolean set, TransDate _date)
{
    TransDate dateRet;

    if (set)
    {
        this.FirstDateSimilarIllnessDay = dayOfMth(_date);
        this.FirstDateSimilarIllnessMonth = mthOfYr(_date);
        this.FirstDateSimilarIllnessYear = year(_date);
    }

    dateRet = mkDate(this.FirstDateSimilarIllnessDay, this.FirstDateSimilarIllnessMonth, this.FirstDateSimilarIllnessYear);

    return dateRet;
}

