/// <summary>
///    Determines whether the specified record is located in the <c>HMClaimTableDetails</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMClaimTableDetails</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMClaimTableDetails</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMClaimTableDetails::exist(_recId))
    {
        ret = checkFailed(strfmt(HMClaimTableDetails::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMClaim1500Table</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>hmClaimTableDetails</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmClaimTableDetails
             where
                 hmClaimTableDetails.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
/// Finds the specified record in the <c>HMClaim1500Table</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
/// A record of the <c>HMClaim1500Table</c> table; otherwise, an empty record.
/// </returns>
public static HMClaimTableDetails find(
    recId   _recId,
    boolean _forUpdate = false,
    ConcurrencyModel        _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClaimTableDetails    hmClaimTableDetails;

    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmClaimTableDetails.concurrencyModel(_concurrencyModel);
    }

    if (_recId)
    {
        hmClaimTableDetails.selectForUpdate(_forUpdate);

        select firstonly hmClaimTableDetails
            where hmClaimTableDetails.RecId == _recId;
    }

    return hmClaimTableDetails;
}

/// <summary>
///    Gets the message to use when a specific record in the <c>HMClaim1500Table</c> does not exist.
/// </summary>
/// <returns>
///    The message that states a record does not exist.
/// </returns>
public static TxtNotExist txtNotExist()
{
    return "@SYS127989";
}
