/// <summary>
/// Gets the communication icons for the current record.
/// </summary>
/// <returns>
/// A communication icon number.
/// </returns>
//BP deviation documented
public display smmQuoteMsgIconNum showTypeIcon()
{
    smmQuoteMsgIconNum iconNum;
    if(this.HMClaimTemplateField == 0)
    {
        iconNum =  1031;
    }
    else
    {
        iconNum = 1030;
    }
    return iconNum;
}

/// <summary>
/// Validates that the specified record exists in the <c>HMClaimTemplateFieldMapping</c> table; if not, an error message is displayed.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean checkExist(recId _recId)
{
    boolean ret = true;

    if (!HMClaimTemplateFieldMapping::exist(_recId))
    {
        ret = checkFailed(strfmt(HMClaimTemplateFieldMapping::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
/// Indicates whether the specified record in the <c>HMClaimTemplateFieldMapping</c> table exists.
/// </summary>
/// <param name="_recId">
/// The unique ID of the record to check for existence.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(recId _recId)
{
    boolean exist;

    exist = (select firstonly
                RecId
            from
                hmClaimTemplateFieldMapping
            where
                hmClaimTemplateFieldMapping.RecId == _recId).RecId != 0;

    return exist;
}

/// <summary>
/// Finds the specified record in the <c>HMClaimTemplateFieldMapping</c> table.
/// </summary>
/// <param name="_recId">
/// The record ID of the record to find.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
/// The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
/// A record of the <c>HMClaimTemplateFieldMapping</c> table; otherwise, an empty record.
/// </returns>
public static HMClaimTemplateFieldMapping find(
    recId   _recId,
    boolean _forUpdate = false,
    ConcurrencyModel        _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClaimTemplateFieldMapping    hmClaimTemplateFieldMapping;

    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmClaimTemplateFieldMapping.concurrencyModel(_concurrencyModel);
    }

    if (_recId)
    {
        hmClaimTemplateFieldMapping.selectForUpdate(_forUpdate);

        select firstonly hmClaimTemplateFieldMapping
            where hmClaimTemplateFieldMapping.RecId == _recId;
    }

    return hmClaimTemplateFieldMapping;
}
