[SysClientCacheDataMethodAttribute(true)]
public display Name clientName()
{
    CustTable custTAble;

    select party from custTAble
    where custTAble.AccountNum == this.ClientAccount;
    return custTAble.name();
}

[SysClientCacheDataMethodAttribute(true)]
public display Description itemAlias()
{
    return this.HMProtocolAlias != '' ? this.HMProtocolAlias : this.itemDescription();
}

[SysClientCacheDataMethodAttribute(true)]
public display Description itemDescription()
{
    InventTable inventTable;
    EcoResProduct ecoResProduct;

    select ItemId, Product from inventTable
    where inventTable.ItemId == this.ItemId;

    return inventTable.itemDescriptionOrName();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        if(this.HMProtocolAlias == '')
        {
            this.HMProtocolAlias = this.itemDescription();
        }
    }
    return ret;
}

public static boolean clientHasProtocol(CustAccount _clientAccount, Itemid _itemid)
{
    HMClientProtocol clientProtocol;

    select count(RecId) from clientProtocol
    where clientProtocol.ClientAccount == _clientAccount
    && clientProtocol.itemid == _itemid;

    return clientProtocol.RecId;
}

public static HMClientProtocol find(CustAccount _clientAccount, Itemid _itemid)
{
    HMClientProtocol clientProtocol;

    select * from clientProtocol
    where clientProtocol.ClientAccount == _clientAccount
    && clientProtocol.itemid == _itemid;

    return clientProtocol;
}

public static HMClientProtocol findRecId(RecId _recId)
{
    HMClientProtocol clientProtocol;

    select * from clientProtocol
    where clientProtocol.RecId == _recId;

    return clientProtocol;
}

public static void updateNotes(CustAccount _clientAccount, Itemid _itemid, Notes _notes, Notes _internalNotes = '', Notes _resultHandlingNotes = '')
{
    HMClientProtocol clientProtocol;

    ttsBegin;

    select forUpdate clientProtocol
    where clientProtocol.ClientAccount == _clientAccount
    && clientProtocol.itemid == _itemid;

    clientProtocol.HMNotes = _notes;
    clientProtocol.HMInternalInstructions = _internalNotes;
    clientProtocol.ResultHandlingNotes = _resultHandlingNotes;
    clientProtocol.update();

    ttsCommit;
}
