[SysClientCacheDataMethodAttribute(true)]
public display HcmPersonnelNumberId getEmployeePersonnelNumber()
{
    HcmWorker               hcmWorkerlocal;

    select PersonnelNumber from hcmWorkerlocal
    where  this.HcmWorker==hcmWorkerlocal.RecId;

    return hcmWorkerlocal.PersonnelNumber;
}

public PersonName getPhysicianName()
{
    HcmWorker               hcmWorkerlocal;
    DirPerson               dirPersonlocal;

    select Name from dirPersonlocal
    where dirPersonlocal.Name != ""
    join recId from  hcmWorkerlocal
    where  this.HcmWorker==hcmWorkerlocal.RecId
    && dirPersonlocal.RecId==hcmWorkerlocal.Person;

    return dirPersonlocal.Name;
}

[SysClientCacheDataMethodAttribute(true)]
public display  Description getPhysicianSpeciality()
{
    HMClinicStaffSpeciality     HMClinicStaffSpeciality;
    HMSpecialtyCode             HMSpecialtyCode;
    Description255              speciality;
 boolean one = true;

    while select * from HMClinicStaffSpeciality
        join * from HMSpecialtyCode
        where HMClinicStaffSpeciality.HMSpecialtyCode == HMSpecialtyCode.RecId
        && HMClinicStaffSpeciality.HMClinicStaff == this.RecId
    {
        if (!one)
        {
            speciality += " , ";
            one = false;
        }
        speciality += HMSpecialtyCode.Description ;
        one = false;

    }


    return speciality;
}

