private boolean isPatternValid(str _pattern, str _strToTest)
{
    System.Text.RegularExpressions.Regex regEx;
    System.Text.RegularExpressions.Match regMatch;
    InteropPermission permission = new InteropPermission(InteropKind::ClrInterop);
    boolean retVal;
    permission.assert();
    //BP Deviation documented
    regEx = new System.Text.RegularExpressions.Regex(_pattern);


    regMatch = regEx.Match(_strToTest);
    retVal = regMatch.get_Success();

    return retVal;
}

/// <summary>
/// Gets the name  of person from <c>DirPerson</c> setup table.
/// </summary>
/// <returns>
/// The name of of person from<c>DirPerson</c> setup table.
/// </returns>
//BP Deviation Documented
[SysClientCacheDataMethodAttribute(true)]
public display PersonName physicianName()
{
    HcmWorker               hcmWorkerlocal;
    DirPerson               dirPersonlocal;

    select Name from dirPersonlocal
    join RecId,Person from hcmWorkerlocal
    where  this.HcmWorker==hcmWorkerlocal.RecId
    && dirPersonlocal.RecId==hcmWorkerlocal.Person;

    return dirPersonlocal.Name;
}

[SysClientCacheDataMethodAttribute(true)]
public display string30 Role()
{
    HMClinicStaffRole  hmClinicStaffRole;

    select RecId, Role from hmClinicStaffRole
        where hmClinicStaffRole.RecId == this.HMClinicStaffRole;

    if (hmClinicStaffRole.RecId)
    {
        return enum2str(hmClinicStaffRole.Role);
    }
    else
    {
        return "";
    }
}

private boolean validateCLIA()
{
    if(this.CLIA != "" && !System.Text.RegularExpressions.Regex::IsMatch(this.CLIA,@"^[a-zA-Z0-9]{10}$"))
    {
        return checkFailed("@MAZ5874");
    }
    return true;
}

private boolean validateDEA()
{

    if( HcmTitle::findByTitle("@MAZ65").RecId == HcmWorkerTitle::findByWorker(HcmWorker::find( this.HcmWorker).RecId).Title)
    {
        if(this.DEA != ""  && !this.isPatternValid( @"^[a-zA-Z]{2}\d{7}$", this.DEA))
        {
            return checkFailed("@MAZ1108");
        }
    }

    return true;
}

private boolean validateFederalId()
{
    if(this.FederalId != "" && !System.Text.RegularExpressions.Regex::IsMatch(this.FederalId,@"^\d{9}$"))
    {
        return checkFailed("@MAZ5875");
    }
    return true;
}

public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);
    if(ret)
    {
        switch(_fieldIdToCheck)
        {
            case fieldnum(HMClinicStaff, DEA) :
                ret = this.validateDEA();
            break;

            case fieldnum(HMClinicStaff, NPI) :
                ret = this.validateNPI();
            break;

            case fieldnum(HMClinicStaff, CLIA) :
                ret = this.validateCLIA();
            break;

            case fieldnum(HMClinicStaff, FederalId) :
                ret = this.validateFederalId();
            break;

            case fieldnum(HMClinicStaff, Taxonomy) :
                ret = this.validateTaxonomy();
            break;

            case fieldnum(HMClinicStaff, UPIN) :
                ret = this.validateUPIN();
            break;

        }
    }

    return ret;
}

private boolean validateLicenseNumber()
{
    if( strLen(this.LicenceNo) > 15 ||  !this.isPatternValid( @"^[a-zA-Z0-9]{1,15}$", this.LicenceNo))
    {
        return checkFailed("@MAZ1109");
    }
    return true;
}

private boolean validateNPI()
{

    if( HcmTitle::findByTitle("@MAZ65").RecId == HcmWorkerTitle::findByWorker(HcmWorker::find(this.HcmWorker).RecId).Title)
    {
        if(this.NPI != "" && !this.isPatternValid( @"^\d{10}$", this.NPI))
        {
            return checkFailed("@MAZ1107");
        }
    }
    return true;
}

private boolean validateTaxonomy()
{
    if(this.Taxonomy != "" && !System.Text.RegularExpressions.Regex::IsMatch(this.Taxonomy,@"^[a-zA-Z0-9]{10}$"))
    {
        return checkFailed("@MAZ5879");
    }
    return true;
}

private boolean validateUPIN()
{
    if(this.UPIN != "" && !System.Text.RegularExpressions.Regex::IsMatch(this.UPIN,@"^[a-zA-Z0-9]{6}$"))
    {
        return checkFailed("@MAZ5878");
    }
    return true;
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMClinicStaff</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMClinicStaff</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMClinicStaff</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMClinicStaff::exist(_recId))
    {
        ret = checkFailed(strfmt(HMClinicStaff::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMClinicStaff</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMClinicStaff</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 hmClinicStaff
             where
                 hmClinicStaff.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMClinicStaff</c> table.
/// </summary>
/// <param name="_physicianRecId">
///    The record ID of the <c>HMPhysician</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMClinicStaff</c> table; otherwise, an empty record.
/// </returns>
public static HMClinicStaff find(
RecId              _physicianRecId,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClinicStaff     hmClinicStaff;

    hmClinicStaff.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmClinicStaff.concurrencyModel(_concurrencyModel);
    }

    select firstonly hmClinicStaff
        where hmClinicStaff.RecId == _physicianRecId;

    return hmClinicStaff;
}

/// <summary>
///    Finds the specified record in the <c>HMClinicStaff</c> table.
/// </summary>
/// <param name="_workerRecId">
///    The record ID of the <c>HMPhysician</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMClinicStaff</c> table; otherwise, an empty record.
/// </returns>
public static HMClinicStaff findByWorker(
RecId              _workerRecId,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClinicStaff     hmClinicStaff;

    hmClinicStaff.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmClinicStaff.concurrencyModel(_concurrencyModel);
    }

    select firstonly hmClinicStaff
        where hmClinicStaff.HcmWorker == _workerRecId;

    return hmClinicStaff;
}

public static PersonName getPhysicianNameByRecId(recId _clinicStaffRecId)
{
    HMClinicStaff           hmClinicStafflocal;
    HcmWorker               hcmWorkerlocal;
    DirPerson               dirPersonlocal;

    select * from dirPersonlocal
    join * from hcmWorkerlocal
    join * from hmClinicStafflocal
    where hmClinicStafflocal.RecId ==_clinicStaffRecId
    && hmClinicStafflocal.HcmWorker == hcmWorkerlocal.RecId
    && hcmWorkerlocal.Person == dirPersonlocal.RecId ;

    return dirPersonlocal.Name;
}

