[SysClientCacheDataMethodAttribute(true)]
display HMProcedureMasterCode CPTCode()
{
    HMProcedureMasterCode  itemDescription = "";

    itemDescription = HMInventTable::findByItemId(this.InventTable).HMProcedureMasterCode;
    return itemDescription;
}

[SysClientCacheDataMethodAttribute(true)]
display HMProcedureMasterDescription serviceDescription()
{
    HMProcedureMasterDescription  itemDescription = "";

    itemDescription = HMInventTableClass::showDescription(this.InventTable);
    return itemDescription;
}

public static HMClinicStaffOrderService find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClinicStaffOrderService  HMClinicStaffOrderService;
    HMClinicStaffOrderService.selectForUpdate(_forUpdate);

    if (_forUpdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMClinicStaffOrderService.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from HMClinicStaffOrderService
            where HMClinicStaffOrderService.RecId == _recid;

    return HMClinicStaffOrderService;
}

public static HMClinicStaffOrderService findbyStaffandService(RecId _clinicStaffRecId,
Itemid                _itemId,
boolean                _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMClinicStaffOrderService  HMClinicStaffOrderService;

    HMClinicStaffOrderService.selectForUpdate(_forUpdate);

    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        HMClinicStaffOrderService.concurrencyModel(_concurrencyModel);
    }

    select firstOnly * from HMClinicStaffOrderService
        where HMClinicStaffOrderService.HMClinicStaff == _clinicStaffRecId
        && HMClinicStaffOrderService.InventTable == _itemId;


    return HMClinicStaffOrderService;
}
