void lookupCollectionGroup(FormControl control)
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    SysTableLookup          sysTableLookup;
    ;

    //Create an instance of SysTableLookup with the form control passed in
    sysTableLookup = SysTableLookup::newParameters(tablenum(HMCollectionGroup), control);

    //Add the fields to be shown in the lookup form
    sysTableLookup.addLookupfield(fieldnum(HMCollectionGroup, CollectionGrp), true);
    sysTableLookup.addLookupfield(fieldnum(HMCollectionGroup, CollectionDesc), false);

    //create the query datasource
    queryBuildDataSource = query.addDataSource(tablenum(HMCollectionGroup));

    //add the query to the lookup form
    sysTableLookup.parmQuery(query);

    // Perform lookup
    sysTableLookup.performFormLookup();
}

//sajirs01102008
public static HMCollectionGroup find(HMCollectionGrp    _collectionGroup,
                                       boolean              _forUpdate = false)
{
    HMCollectionGroup    colGroup;
    ;

    if (_collectionGroup)
    {
        colGroup.selectForUpdate(_forUpdate);

        select firstonly colGroup
            index hint collectionGroupIdx
            where colGroup.CollectionGrp == _collectionGroup;
    }

    return colGroup;
}
