[SysClientCacheDataMethodAttribute(true)]
public display String255 showElementLabel()
{
    DictEnum enum;
    str      label;

    if (this.RecId)
    {
        enum = new DictEnum(enumName2Id(this.ElementName));
        label = enum.label();
    }
    return label;
}

/// <summary>
///    Determines whether the specified record is located in the <c>HMColourCodingParameters</c> table.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMColourCodingParameters</c> table record to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
/// <remarks>
///    If the specified record is not located in the <c>HMColourCodingParameters</c> table, an error message is
///    displayed.
/// </remarks>
public static boolean checkExist(RecId _recId)
{
    boolean ret = true;

    if (!HMColourCodingParameters::exist(_recId))
    {
        ret = checkFailed(strfmt(HMColourCodingParameters::txtNotExist(), _recId));
    }

    return ret;
}

/// <summary>
///    Determines whether the specified record in the <c>HMColourCodingParameters</c> table exists.
/// </summary>
/// <param name="_recId">
///    The record ID of the <c>HMColourCodingParameters</c> table to check if it exists.
/// </param>
/// <returns>
///    true if the specified record exists; otherwise, false.
/// </returns>
public static boolean exist(RecId _recId)
{
    boolean found;

    found = (select firstonly
                 RecId
             from
                 HMColourCodingParameters
             where
                 HMColourCodingParameters.RecId == _recId).RecId != 0;

    return found;
}

/// <summary>
///    Finds the specified record in the <c>HMColourCodingParameters</c> table.
/// </summary>
/// <param name="_recid">
///    The record ID of the <c>HMColourCodingParameters</c> record to find.
/// </param>
/// <param name="_forUpdate">
///    A Boolean value that indicates whether to read the record for update; optional.
/// </param>
/// <param name="_concurrencyModel">
///    The concurrency model option to use when reading the record; optional.
/// </param>
/// <returns>
///    A record in the <c>HMColourCodingParameters</c> table; otherwise, an empty record.
/// </returns>
public static HMColourCodingParameters find(
RecId               _recid,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMColourCodingParameters   hmColourCodingParameters;

    hmColourCodingParameters.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmColourCodingParameters.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmColourCodingParameters
        where hmColourCodingParameters.RecId == _recid;

    return hmColourCodingParameters;
}

