/// </param>
/// <returns>
///    A record in the <c>HMColourCodingParameters</c> table; otherwise, an empty record.
/// </returns>
public static HMColourCodingParameters findByElementIdAndValue(
integer  _elementId ,
String255  _elementValue ,
boolean             _forUpdate = false,
ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    HMColourCodingParameters   hmColourCodingParameters;

    hmColourCodingParameters.selectForUpdate(_forUpdate );
    if (_forUpdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        hmColourCodingParameters.concurrencyModel(_concurrencyModel);
    }

    select firstonly * from hmColourCodingParameters
        where hmColourCodingParameters.ElementId == _elementId
        && hmColourCodingParameters.ElementValue == _elementValue;

    return hmColourCodingParameters;
}

