public void delete()
{
    HMComplementContactParameters HMComplementContactParametersM123;
    HMComplementContactParameters HMComplementContactParametersMNow;

    if(this.ComplementType == HMComplementType::M1)
    {
        select forUpdate  HMComplementContactParametersM123
        where HMComplementContactParametersM123.ComplementType == HMComplementType::M123
        && HMComplementContactParametersM123.ComplementContacts == this.ComplementContacts;

        select forUpdate  HMComplementContactParametersMNow
        where HMComplementContactParametersMNow.ComplementType == HMComplementType::MedisysNow
        && HMComplementContactParametersMNow.ComplementContacts == this.ComplementContacts;
    }

    super();
    if(HMComplementContactParametersM123)
    {
        HMComplementContactParametersM123.delete();
    }
    if(HMComplementContactParametersMNow)
    {
       HMComplementContactParametersMNow.delete();
    }
}

public void insert()
{
    HMComplementContactParameters HMComplementContactParametersM123;
    HMComplementContactParameters HMComplementContactParametersMNow;
    super();
    if(this.ComplementType == HMComplementType::M1)
    {
        buf2Buf(this,HMComplementContactParametersM123);
        HMComplementContactParametersM123.ComplementType = HMComplementType::M123;
        HMComplementContactParametersM123.insert();

        buf2Buf(this,HMComplementContactParametersMNow);
        HMComplementContactParametersMNow.ComplementType = HMComplementType::MedisysNow;
        HMComplementContactParametersMNow.insert();
    }
}

public void update()
{
    HMComplementContactParameters HMComplementContactParametersM123;
    HMComplementContactParameters HMComplementContactParametersMNow;
    super();
    if(this.ComplementType == HMComplementType::M1)
    {
        ttsBegin;
        select forUpdate  HMComplementContactParametersM123
        where HMComplementContactParametersM123.ComplementType == HMComplementType::M123
        && HMComplementContactParametersM123.ComplementContacts == this.ComplementContacts;
        if(HMComplementContactParametersM123)
        {
            buf2Buf(this,HMComplementContactParametersM123);
            HMComplementContactParametersM123.ComplementType = HMComplementType::M123;
            HMComplementContactParametersM123.update();
        }

        select forUpdate  HMComplementContactParametersMNow
        where HMComplementContactParametersMNow.ComplementType == HMComplementType::MedisysNow
        && HMComplementContactParametersMNow.ComplementContacts == this.ComplementContacts;
        if(HMComplementContactParametersMNow)
        {
            buf2Buf(this,HMComplementContactParametersMNow);
            HMComplementContactParametersMNow.ComplementType = HMComplementType::MedisysNow;
            HMComplementContactParametersMNow.update();
        }
        ttsCommit;
    }
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        if(this.ContactType == HMComplementContactType::Email)
        {
            if(this.EmailId == '')
            {
                ret = checkFailed("@MAZ12481");
            }
        }
        if(this.ComplementType != HMComplementType::OccupationalHealth)
        {
            if(this.ComplementContactTarget == HMComplementContactTarget::OHComplement)
            {
                ret = checkFailed("@MAZ12486");
            }
        }
    }
    return ret;
}

public static HMComplementContactParameters getNextComplementRecord(HMComplementContacts  _complementContact, HMComplementType _complementType, HMComplementEHType _complementEhType = HMComplementEHType::None)
{
    HMComplementContactParameters hmComplementContactParameters;
    HMComplementContacts nextComplementContact = _complementContact + 1;

    select * from hmComplementContactParameters
    where hmComplementContactParameters.ComplementContacts == nextComplementContact
    && hmComplementContactParameters.ComplementType == _complementType
    && hmComplementContactParameters.EHSequenceType == _complementEhType;

    return hmComplementContactParameters;
}
